import{_ as d,o as c,c as o,a}from"./app.f0c02a19.js";const i="/assets/v2-5aa61d5578e48dc26c1930e477781734_hd.9e6bf425.jpeg",e="/assets/v2-c4bcf2115869e938f58bff5a86e35313_hd.37488f11.jpeg",p="/assets/v2-22899535872176cf546df95edc99a98b_hd.7b0d422b.jpeg",l="/assets/v2-b66bfc968ea01f4fcbfe899bde80cefb_hd.7ccb9d85.jpeg",t="/assets/v2-1f51ff95d5d3b7a8fb833c7016cf348e_hd.f699dad6.jpeg",r={},n=a('<h1 id="https-\u964D\u7EA7\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#https-\u964D\u7EA7\u653B\u51FB" aria-hidden="true">#</a> HTTPS \u964D\u7EA7\u653B\u51FB</h1><h2 id="https" tabindex="-1"><a class="header-anchor" href="#https" aria-hidden="true">#</a> HTTPS</h2><p>\u5728\u4F20\u7EDF\u6D41\u884C\u7684 WEB \u670D\u52A1\u4E2D\uFF0C\u7531\u4E8E HTTP \u534F\u8BAE\u6CA1\u6709\u5BF9\u6570\u636E\u5305\u8FDB\u884C\u52A0\u5BC6\uFF0C\u5BFC\u81F4 HTTP \u534F\u8BAE\u4E0B\u7684\u7F51\u7EDC\u5305\u662F\u660E\u6587\u4F20\u8F93\uFF0C\u6240\u4EE5\u53EA\u8981\u653B\u51FB\u8005\u62E6\u622A\u5230 HTTP \u534F\u8BAE\u4E0B\u7684\u6570\u636E\u5305\uFF0C\u5C31\u80FD\u76F4\u63A5\u7AA5\u63A2\u8FD9\u4E9B\u7F51\u7EDC\u5305\u7684\u6570\u636E\u3002HTTPS \u534F\u8BAE\u5C31\u662F\u6765\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u7684\u3002</p><p>\u7B80\u800C\u8A00\u4E4B\uFF0CHTTPS \u534F\u8BAE\u5728\u5E94\u7528\u5C42\u7684 HTTP \u534F\u8BAE\u548C\u4EE5\u53CA\u4F20\u8F93\u5C42 TCP \u534F\u8BAE\u4E4B\u95F4\u7684\u4F1A\u8BDD\u5C42\u52A0\u5165\u4E86 <code>SSL/TLS</code> \u534F\u8BAE\uFF0C\u7528\u4F5C\u52A0\u5BC6\u6570\u636E\u5305\u3002\u4F1A\u8BDD\u5C42\u7684\u8FD9\u4E2A\u52A0\u5BC6\u534F\u8BAE\uFF0C\u5728\u771F\u6B63\u6570\u636E\u4F20\u8F93\u4E4B\u524D\uFF0C\u8FDB\u884C\u63E1\u624B\u901A\u4FE1\uFF0C\u63E1\u624B\u7684\u4FE1\u606F\u4EA4\u6362\u4E3B\u8981\u5305\u62EC\uFF1A\u53CC\u65B9\u786E\u8BA4\u52A0\u5BC6\u534F\u8BAE\u7684\u7248\u672C\uFF0C\u786E\u8BA4\u8EAB\u4EFD\uFF08\u5176\u4E2D\u6D4F\u89C8\u5668\u5BA2\u6237\u7AEF\u7684\u8EAB\u4EFD\u6709\u65F6\u662F\u4E0D\u9700\u8981\u786E\u8BA4\u7684\uFF09\uFF0C\u786E\u8BA4\u4F20\u8F93\u771F\u6B63\u6570\u636E\u7684\u52A0\u5BC6\u5BC6\u7801\uFF08\u5BF9\u79F0\u52A0\u5BC6\uFF09\u3002</p><p>\u5728\u7528\u4F5C\u4FE1\u606F\u4F20\u8F93\u7684\u52A0\u5BC6\u5BC6\u7801\u786E\u8BA4\u4E4B\u540E\uFF0C\u63A5\u4E0B\u6765\u7684\u901A\u4FE1\u6570\u636E\u90FD\u4F1A\u52A0\u5BC6\u540E\u518D\u4F20\u8F93\uFF0C\u4ECE\u800C\u8FBE\u5230\u9632\u7A83\u53D6\u7684\u4F5C\u7528\u3002</p><h2 id="https-\u534F\u8BAE\u964D\u7EA7" tabindex="-1"><a class="header-anchor" href="#https-\u534F\u8BAE\u964D\u7EA7" aria-hidden="true">#</a> HTTPS \u534F\u8BAE\u964D\u7EA7</h2><p>\u90A3\u4E48\u4F7F\u7528\u4E86 HTTPS \u5C31\u80FD\u786E\u4FDD\u5B89\u5168\u4F20\u8F93\u4E86\u5417\uFF1F</p><p>\u53EF\u4EE5\u8BF4\u7406\u60F3\u4E0A\u662F\u7684\uFF0C\u73B0\u5B9E\u5374\u4E0D\u662F\u3002</p><p>\u539F\u56E0\u7B80\u5355\u6765\u8BF4\u5C31\u662F\uFF1A\u8BBE\u8BA1\u548C\u5B9E\u73B0 <code>SSL/TLS</code> \u534F\u8BAE\u51FA\u73B0\u4E86\u6F0F\u6D1E\uFF0C\u5BFC\u81F4\u653B\u51FB\u8005\u540C\u6837\u53EF\u4EE5\u653B\u51FB\u4E00\u4E9B\u65E7\u7248\u672C\u7684 <code>SSL/TLS</code> \u534F\u8BAE\u3002\u5F53 <code>SSL/TLS</code> \u52A0\u5BC6\u534F\u8BAE\u6F0F\u6D1E\u7206\u51FA\u6765\u4E4B\u540E\uFF0C\u6700\u65B0\u7248\u672C\u7684 <code>SSL/TLS</code> \u534F\u8BAE\u4FEE\u590D\u4E86\u5DF2\u77E5\u6F0F\u6D1E\uFF0C\u4F46\u65E7\u7248\u672C\u7684\u52A0\u5BC6\u534F\u8BAE\u5374\u53D8\u5F97\u4E0D\u5B89\u5168\u4E86\u3002</p><p><strong>\u90A3\u4E48\u4EC0\u4E48\u53C8\u662F\u201CHTTPS\u534F\u8BAE\u964D\u7EA7\u201D\uFF1F</strong></p><p>\u5728\u4E0A\u4E00\u5C0F\u8282\u6211\u4EEC\u63D0\u5230 <code>SSL/TLS</code> \u534F\u8BAE\u901A\u8FC7\u63E1\u624B\u6765\u786E\u5B9A\u901A\u4FE1\u4FE1\u606F\uFF0C\u5176\u4E2D\u63E1\u624B\u53CC\u65B9\u8981\u7EDF\u4E00\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\u3002\u5728\u63E1\u624B\u8FC7\u7A0B\u4E2D\u8FD9\u6837\u786E\u8BA4\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\uFF1A</p><ul><li>\u7531\u5BA2\u6237\u7AEF\uFF08\u5982\u6D4F\u89C8\u5668\uFF09\u53D1\u9001\u7B2C\u4E00\u4E2A\u6570\u636E\u5305 <code>ClientHello</code>\uFF0C\u8FD9\u4E2A\u6570\u636E\u5305\u4E2D\u4FDD\u5B58\u7740\u5BA2\u6237\u7AEF\u652F\u6301\u7684\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\u3002</li><li>\u670D\u52A1\u5668\u6536\u5230\u8FD9\u4E2A <code>ClientHello</code> \u6570\u636E\u5305\uFF0C\u67E5\u770B\u91CC\u9762\u5BA2\u6237\u7AEF\u652F\u6301\u7684\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\uFF0C\u7136\u540E\u5339\u914D\u670D\u52A1\u5668\u81EA\u5DF1\u652F\u6301\u7684\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\uFF0C\u4ECE\u800C\u786E\u8BA4\u53CC\u65B9\u5E94\u8BE5\u7528\u7684\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\u3002</li><li>\u670D\u52A1\u5668\u53D1\u9001 <code>ServerHello</code> \u6570\u636E\u5305\u7ED9\u5BA2\u6237\u7AEF\uFF0C\u544A\u8BC9\u5BA2\u6237\u7AEF\u8981\u4F7F\u7528\u4EC0\u4E48\u52A0\u5BC6\u534F\u8BAE\u7248\u672C\u3002</li></ul><p>\u5728\u4E0A\u8FF0\u8FC7\u7A0B\u4E2D\uFF0C\u5982\u679C\u5BA2\u6237\u7AEF\u53D1\u9001\u7ED9\u670D\u52A1\u5668\u7684 <code>ClientHello</code> \u6570\u636E\u5305\u4E2D\u8BF4\u81EA\u5DF1\u4EC5\u652F\u6301\u67D0\u4E2A\u6709\u6F0F\u6D1E\u7684\u65E7\u7248\u672C\u52A0\u5BC6\u534F\u8BAE\uFF08\u6BD4\u5982\u4EC5\u652F\u6301 <code>SSLv3.0</code>\uFF09,\u670D\u52A1\u5668\u6709\u4E24\u79CD\u53EF\u80FD\uFF1A</p><ul><li>\u670D\u52A1\u5668\u652F\u6301\u5F88\u591A\u7248\u672C\uFF0C\u5176\u4E2D\u5305\u62EC\u6709\u6F0F\u6D1E\u7684\u65E7\u7248\u672C\u548C\u65B0\u7248\u672C\uFF08\u5305\u62EC\u4E86 <code>SSLv3.0</code> \u534F\u8BAE\uFF09\uFF0C\u90A3\u4E48\u670D\u52A1\u5668\u4F1A\u8BA4\u53EF\u4F7F\u7528\u6709\u6F0F\u6D1E\u7684\u65E7\u7248\u672C\u534F\u8BAE\uFF0C\u4ECE\u800C\u544A\u8BC9\u5BA2\u6237\u7AEF\u4F7F\u7528\u6709\u6F0F\u6D1E\u7684\u65E7\u7248\u672C\uFF08\u53EF\u4EE5\u4F7F\u7528 <code>SSLv3.0</code>\uFF09\u3002</li><li>\u670D\u52A1\u5668\u4E0D\u652F\u6301\u6709\u6F0F\u6D1E\u7684\u65E7\u7248\u672C\uFF0C\u62D2\u7EDD\u5BA2\u6237\u7AEF\u7684\u8FD9\u6B21\u8BF7\u6C42\uFF0C\u63E1\u624B\u5931\u8D25\u3002</li></ul><p>\u5BF9\u4E8E\u653B\u51FB\u8005\uFF0C\u4F5C\u4E3A\u4E2D\u95F4\u4EBA\u53EA\u80FD\u76D1\u542C\u5230\u52A0\u5BC6\u8FC7\u7684\u6570\u636E\uFF0C\u5982\u679C\u8FD9\u4E9B\u6570\u636E\u901A\u8FC7\u6CA1\u6709\u6F0F\u6D1E\u7684\u52A0\u5BC6\u7248\u672C\u52A0\u5BC6\uFF0C\u653B\u51FB\u8005\u5E76\u4E0D\u80FD\u505A\u4EC0\u4E48\u3002</p><p>\u4F46\u662F\uFF0C\u5982\u679C\u670D\u52A1\u5668\u63D0\u4F9B\u6709\u6F0F\u6D1E\u7684\u65E7\u7248\u672C\u52A0\u5BC6\u534F\u8BAE\u7684\u652F\u6301\uFF0C\u800C\u540C\u65F6\u653B\u51FB\u8005\u53C8\u80FD\u4F5C\u4E3A\u4E2D\u95F4\u4EBA\u63A7\u5236\u88AB\u653B\u51FB\u8005\u7684\u6D4F\u89C8\u5668\u53D1\u8D77\u6F0F\u6D1E\u7248\u672C\u7684 HTTPS \u8BF7\u6C42\uFF0C\u90A3\u867D\u7136\u653B\u51FB\u8005\u76D1\u542C\u5230\u7684\u4E5F\u662F\u52A0\u5BC6\u8FC7\u7684\u6570\u636E\uFF0C\u4F46\u56E0\u4E3A\u52A0\u5BC6\u534F\u8BAE\u6709\u6F0F\u6D1E\uFF0C\u53EF\u4EE5\u89E3\u5BC6\u8FD9\u4E9B\u6570\u636E\uFF0C\u6240\u4EE5\u6570\u636E\u5C31\u548C\u660E\u6587\u4F20\u8F93\u6CA1\u6709\u4EC0\u4E48\u5DEE\u522B\u4E86\u3002</p><p><strong>\u8FD9\u5C31\u662FHTTPS\u534F\u8BAE\u964D\u7EA7\u3002</strong></p><h2 id="sslv3-0-\u7684-aes-\u52A0\u5BC6\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#sslv3-0-\u7684-aes-\u52A0\u5BC6\u539F\u7406" aria-hidden="true">#</a> SSLv3.0 \u7684 AES \u52A0\u5BC6\u539F\u7406</h2><p>\u653B\u51FB\u8005\u901A\u8FC7\u63E1\u624B\u5C06 HTTPS \u901A\u4FE1\u534F\u8BAE\u964D\u4F4E\u5230 SSLv3.0 \u4E4B\u540E\uFF0C\u5C06\u4F1A\u62E6\u622A\u5230\u7ECF\u8FC7 SSLv3.0 \u52A0\u5BC6\u8FC7\u7684\u6570\u636E\uFF0CPadding Oracle \u653B\u51FB\u53EF\u4EE5\u89E3\u5BC6\u8FD9\u4E9B\u6570\u636E\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u53EB Padding</strong>\uFF0C\u6211\u4EEC\u4ECE SSLv3.0 \u7684\u52A0\u5BC6\u539F\u7406\u8BF4\u8D77\u3002</p><p>\u4E5F\u662F\u5728\u7B2C\u4E00\u5C0F\u8282\uFF0CHTTPS \u63E1\u624B\u8FC7\u7A0B\u4E2D\uFF0C\u901A\u4FE1\u53CC\u65B9\u8FD8\u786E\u8BA4\u4E86\u4E00\u4E2A\u201C\u52A0\u5BC6\u5BC6\u7801\u201D\uFF0C\u8FD9\u4E2A\u5BC6\u7801\u662F\u53CC\u65B9\u5728\u63E1\u624B\u8FC7\u7A0B\u4E2D\u4F7F\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u65B9\u5F0F\u534F\u5546\u51FA\u6765\u7684\u5BF9\u79F0\u52A0\u5BC6\u5BC6\u7801\u3002\u653B\u51FB\u8005\u80FD\u62E6\u622A\u5230\u7684\u6570\u636E\u5C31\u662F\u88AB\u8FD9\u4E2A\u5BC6\u7801\u52A0\u5BC6\u7684\u3002</p><p>\u8FD9\u4E2A\u5BF9\u79F0\u52A0\u5BC6\u4F7F\u7528 AES \u52A0\u5BC6\uFF0CAES \u5757\u5BC6\u7801\u4F1A\u628A\u8981\u52A0\u5BC6\u7684\u660E\u6587\u5207\u5206\u6210\u4E00\u4E2A\u4E2A\u6574\u9F50\u7684\u5757\uFF08\u5982\u5C06\u660E\u6587\u4EE5 16 \u4E2A\u5B57\u8282\u4E3A\u4E00\u5757\u5206\u6210\u5F88\u591A\u5757\uFF09\uFF0C\u5982\u679C\u6700\u540E\u4E00\u5757\u4E0D\u8DB3\u4E00\u5757\uFF0C\u5219\u4F1A\u586B\u5145\uFF08Padding\uFF09\u6EE1\u4E00\u5757\uFF0C\u518D\u8FDB\u884C\u52A0\u5BC6\u3002\u8FD9\u4E2A\u586B\u5145\u5C31\u662F Padding Oracle \u653B\u51FB\u7684\u5173\u952E\u3002</p><p>AES \u662F\u5178\u578B\u7684\u5757\u5BC6\u7801\uFF0C\u5757\u5BC6\u7801\u7684\u52A0\u5BC6\u65B9\u5F0F\u6709\u5F88\u591A\u79CD\uFF0C\u5982\u679C\u4F60\u4E0D\u4E86\u89E3\uFF0C\u53EF\u4EE5\u67E5\u770B\u5757\u5BC6\u7801\u7684\u5DE5\u4F5C\u6A21\u5F0F\u3002</p><p>SSLv3.0 \u4E2D\u4F7F\u7528 <code>AES-CBC</code> \u6A21\u5F0F\u52A0\u5BC6\uFF0C\u6211\u4EEC\u6765\u770B\u52A0\u5BC6\u548C\u89E3\u5BC6\u6D41\u7A0B\uFF1A</p><h3 id="\u52A0\u5BC6\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u52A0\u5BC6\u8FC7\u7A0B" aria-hidden="true">#</a> \u52A0\u5BC6\u8FC7\u7A0B</h3><p><img src="'+i+'" alt="img"></p><ul><li>\u660E\u6587\u9996\u5148\u88AB\u5206\u6210\u5F88\u591A\u660E\u6587\u5757\uFF0C\u6240\u6709\u660E\u6587\u5757\u7684\u5B57\u8282\u957F\u5EA6\u90FD\u4E00\u6837\uFF0C\u5176\u4E2D\u6700\u540E\u4E00\u4E2A\u660E\u6587\u5757\u7ECF\u8FC7\u4E86\u586B\u5145\uFF0C\u82E5\u5047\u8BBE\u6700\u540E\u4E00\u4E2A\u586B\u5145\u5B57\u8282\u503C\u4E3A L\uFF0C\u5219\u586B\u5145\u5185\u5BB9\u4E3A\u503C\u4E3AL\u7684\u5B57\u8282\u91CD\u590D L \u6B21\uFF08\u4E0D\u5305\u62EC\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u672C\u8EAB\uFF09 \u3002</li><li>\u52A0\u5BC6\u4ECE\u7B2C\u4E00\u4E2A\u660E\u6587\u5757\u5F00\u59CB\u94FE\u5F0F\u4F9D\u6B21\u8FDB\u884C\uFF0C\u5176\u4E2D\uFF0C\u7B2C\u4E00\u4E2A\u660E\u6587\u5757\u5148\u548C\u521D\u59CB\u5316\u7684\u5411\u91CF\u8FDB\u884C\u5F02\u6216\uFF0C\u4E4B\u540E\u4F7F\u7528\u52A0\u5BC6 key \u52A0\u5BC6\uFF0C\u751F\u6210\u7B2C\u4E00\u4E2A\u5BC6\u6587\u5757\u3002</li><li>\u5C06\u7B2C\u4E00\u4E2A\u5BC6\u6587\u5757\u4E0E\u7B2C\u4E8C\u4E2A\u660E\u6587\u5757\u5F02\u6216\uFF0C\u7136\u540E\u4F7F\u7528\u52A0\u5BC6 key \u52A0\u5BC6\uFF0C\u751F\u6210\u7B2C\u4E8C\u4E2A\u5BC6\u6587\u5757\u3002</li><li>\u4EE5\u6B64\u7C7B\u63A8\uFF0C\u7B2C N \u4E2A\u5BC6\u6587\u5757\uFF0C\u7531\u7B2C N-1 \u4E2A\u5BC6\u6587\u5757\u548C\u7B2C N \u4E2A\u660E\u6587\u5757\u5F02\u6216\uFF0C\u7136\u540E\u4F7F\u7528\u52A0\u5BC6 key \u52A0\u5BC6\u83B7\u5F97\u3002</li><li>\u5C06\u6240\u6709\u83B7\u5F97\u7684\u5BC6\u6587\u5757\u4F9D\u6B21\u62FC\u63A5\u8D77\u6765\uFF0C\u5C31\u5F97\u5230\u4E86\u6700\u540E\u7684\u52A0\u5BC6\u6570\u636E\uFF0C\u8FD9\u4E2A\u6570\u636E\u662F\u66B4\u9732\u5728\u7F51\u7EDC\u6D41\u91CF\u4E2D\u7684\u6570\u636E\uFF0C\u4E5F\u662F\u653B\u51FB\u8005\u53EF\u80FD\u622A\u83B7\u7684\u6570\u636E\u3002</li></ul><h3 id="\u89E3\u5BC6\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u89E3\u5BC6\u8FC7\u7A0B" aria-hidden="true">#</a> \u89E3\u5BC6\u8FC7\u7A0B</h3><p><img src="'+e+'" alt="img"></p><ul><li>\u5C06\u5BC6\u6587\u5185\u5BB9\u5206\u4E3A\u82E5\u5E72\u4E2A\u5BC6\u6587\u5757\uFF0C\u6BCF\u4E2A\u5BC6\u6587\u5757\u548C\u52A0\u5BC6\u65F6\u7684\u660E\u6587\u5757\u957F\u5EA6\u4E00\u6837\uFF0C\u6B64\u65F6\u7531\u4E8E\u52A0\u5BC6\u65F6\u7ECF\u8FC7\u4E86\u586B\u5145\uFF0C\u5BC6\u6587\u5185\u5BB9\u80AF\u5B9A\u80FD\u6574\u9F50\u7684\u5206\u5272\u6210\u6574\u6570\u4E2A\u5BC6\u6587\u5757\u3002</li><li>\u5BF9\u4E8E\u7B2C\u4E00\u4E2A\u5BC6\u6587\u5757\uFF0C\u4F7F\u7528\u52A0\u5BC6 key \u89E3\u5BC6\u4E4B\u540E\uFF0C\u4E0E\u52A0\u5BC6\u65F6\u7684\u521D\u59CB\u5316\u54CD\u4EAE\u5F02\u6216\u83B7\u5F97\u7B2C\u4E00\u4E2A\u660E\u6587\u5757\u3002</li><li>\u5BF9\u4E8E\u5176\u4ED6\u7684\u5BC6\u6587\u5757\uFF0C\u5982\u7B2C N \u4E2A\uFF0C\u4F7F\u7528\u52A0\u5BC6 key \u89E3\u5BC6\u4E4B\u540E\uFF0C\u4E0E\u7B2C N-1 \u4E2A\u5BC6\u6587\u5757\u5F02\u6216\uFF0C\u83B7\u5F97\u76F8\u5E94\u7684\u660E\u6587\u3002</li></ul><p><strong>\u8FD9\u5C31\u662F SSLv3.0 \u7684\u52A0\u5BC6\u539F\u7406\u8FC7\u7A0B\u3002</strong></p><h2 id="cbc-\u6A21\u5F0F\u52A0\u5BC6\u7684-padding-oracle-\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#cbc-\u6A21\u5F0F\u52A0\u5BC6\u7684-padding-oracle-\u653B\u51FB" aria-hidden="true">#</a> CBC \u6A21\u5F0F\u52A0\u5BC6\u7684 Padding Oracle \u653B\u51FB</h2><p>\u4E0A\u4E00\u8282\u6211\u4EEC\u89E3\u91CA\u4E86 CBC \u6A21\u5F0F\u52A0\u5BC6\u8FC7\u7A0B\uFF0C\u8FD9\u79CD\u6A21\u5F0F\u4F7F\u7528\u4E0D\u5F53\u4F1A\u906D\u5230\u9488\u5BF9\u4E8E Padding \u7684\u653B\u51FB\u3002</p><p>\u5BF9\u4E8E SSL \u534F\u8BAE\uFF0C\u9700\u8981\u52A0\u5BC6\u7684\u6570\u636E\u5305\u62EC\u4FE1\u606F\u672C\u8EAB\u548C\u4FE1\u606F\u7684MAC\u6458\u8981\u503C\uFF0C\u5728\u534F\u8BAE\u8BBE\u8BA1\u521D\u671F\uFF0C\u7531\u4E8E\u5927\u5BB6\u8003\u8651\u4E0D\u5468\uFF0C\u4F7F\u7528\u4E86\u201C\u5148\u505A\u4FE1\u606F\u6458\u8981 MAC DATA\uFF0C\u518D\u505A\u52A0\u5BC6\u201D\u7684\u65B9\u5F0F\uFF08MAC-then-encrypt\uFF09\u3002</p><p>MAC-then-encrypt\uFF0C\u8FD9\u79CD\u65B9\u5F0F\u53EF\u80FD\u906D\u5230 Padding Oracle \u653B\u51FB\u3002SSL \u534F\u8BAE\u5728\u8FD9\u79CD\u65B9\u5F0F\u4E0B\uFF0C\u6570\u636E\u683C\u5F0F\u5982\u56FE\uFF1A</p><p><img src="'+p+'" alt="img"></p><p>\u8FD9\u662F\u4E00\u6B21\u8BF7\u6C42\u8981\u4F20\u9012\u7684\u6570\u636E\u7ED3\u6784\u793A\u610F\uFF0C\u5176\u4E2D DATA \u4E3A\u6700\u91CD\u8981\u7684\u6570\u636E\uFF0C\u5305\u62EC COOKIE \u751A\u81F3\u7528\u6237\u540D\u5BC6\u7801\u7B49\u4FE1\u606F\uFF0CHMAC \u5982\u56FE\u793A\u662F DATA \u4EE5\u53CA\u5176\u4ED6\u4E00\u4E9B\u5E8F\u5217\u6570\u636E\u7684 MAC \u6458\u8981\uFF0C\u6700\u540E\u662F\u8865\u5145\u5B57\u8282\u7684 Padding\u3002</p><p>\u5728\u8FD9\u79CD\u6570\u636E\u7ED3\u6784\u4E0B\uFF0C\u52A0\u5BC6\u6570\u636E\u4F20\u8F93\u5230\u63A5\u6536\u8005\u624B\u91CC\uFF0C\u4F1A\u89E3\u5BC6\u7136\u540E\u4F9D\u6B21\u9A8C\u8BC1 Padding \u6570\u636E\u548C HMAC \u6570\u636E\uFF0C\u6765\u786E\u8BA4\u6570\u636E\u662F\u6B63\u786E\u7684\u3002</p><p>\u56E0\u6B64\uFF0C\u63A5\u6536\u8005\u89E3\u5BC6\u9A8C\u8BC1\u65F6\u4E3B\u8981\u6709\u4E09\u79CD\u53EF\u80FD\u53D1\u751F\u7684\u60C5\u51B5\uFF1A</p><ul><li>Padding \u6570\u636E\u9519\u8BEF\uFF0C\u62D2\u7EDD\uFF0C\u8FD4\u56DE\u3002</li><li>HMAC \u6570\u636E\u9519\u8BEF\uFF0C\u62D2\u7EDD\uFF0C\u8FD4\u56DE\u3002</li><li>\u6B63\u786E\u63A5\u6536\u3002</li></ul><p>\u8FD9 3 \u79CD\u8FD4\u56DE\u7ED9\u4E88\u4E86\u653B\u51FB\u8005\u542F\u793A\uFF0C\u6211\u4EEC\u518D\u6765\u770B\u89E3\u5BC6\u8FC7\u7A0B\uFF1A</p><p><img src="'+e+'" alt="img"></p><p>\u7559\u610F\u6700\u540E\u4E00\u4E2A\u5BC6\u6587\u5757\uFF0C\u8FD9\u4E2A\u5BC6\u6587\u5757\u9996\u5148\u4F1A\u7528\u52A0\u5BC6 key \u8FDB\u884C\u89E3\u5BC6\uFF0C\u7136\u540E\u4E0E\u5012\u6570\u7B2C\u4E8C\u4E2A\u5BC6\u6587\u5757\u8FDB\u884C\u5F02\u6216\u3002\u653B\u51FB\u8005\u662F\u80FD\u591F\u622A\u83B7\u5230\u6240\u6709\u7684\u5BC6\u6587\u5185\u5BB9\u7684\u3002</p><p>\u6211\u4EEC\u63D0\u5230\u8FC7 Padding \u6570\u636E\u7684\u89C4\u5219\u662F\uFF1A\u82E5\u5047\u8BBE\u6700\u540E\u4E00\u4E2A\u586B\u5145\u5B57\u8282\u503C\u4E3A L\uFF0C\u5219\u586B\u5145\u5185\u5BB9\u4E3A\u503C\u4E3AL\u7684\u5B57\u8282\u91CD\u590D L \u6B21\uFF08\u4E0D\u5305\u62EC\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u672C\u8EAB\uFF09 \u3002\u800C\u9A8C\u8BC1 Padding \u7684\u8FC7\u7A0B\u4E5F\u662F\u6309\u7167\u89C4\u5219\u6765\u7684\uFF1A\u8BFB\u53D6\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u7684\u503C\uFF0C\u5E76\u79FB\u9664\u6700\u540E\u4E00\u4E2A\u5B57\u8282\uFF0C\u7136\u540E\u9A8C\u8BC1\u5269\u4E0B\u7684 Padding \u4E3A L \u4E2A\u503C\u4E3A L \u7684\u5B57\u8282\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5F53\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u503C\u4E3A 0x00 \u7684\u65F6\u5019\uFF0CPadding \u9A8C\u8BC1\u4F1A\u76F4\u63A5\u901A\u8FC7\u3002</p><p>\u5047\u8BBE\uFF1A</p><ul><li>\u5012\u6570\u7B2C\u4E8C\u4E2A\u5BC6\u6587\u5757\u7684\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u503C\u4E3A x\uFF08\u653B\u51FB\u8005\u53EF\u89C1\u7684\uFF09\u3002</li><li>\u6700\u540E\u4E00\u4E2A\u5BC6\u6587\u5757\uFF0C\u89E3\u5BC6\u540E\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u503C\u4E3A a (\u653B\u51FB\u8005\u4E0D\u53EF\u89C1\uFF09\u3002</li><li>\u6700\u540E\u4E00\u4E2A\u660E\u6587\u5757\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u4E3A m \uFF08\u653B\u51FB\u8005\u4E0D\u53EF\u89C1\uFF09\u3002</li></ul><p><img src="'+l+'" alt="img"></p><p>\u53EF\u77E5\uFF0C <code>a = x</code> \u5F02\u6216 <code>m</code> \u5E76\u4E14 <code>m = x</code> \u5F02\u6216 <code>a</code>\u3002</p><p>\u7136\u540E\u653B\u51FB\u8005\u4E0D\u65AD\u4FEE\u6539 <code>x</code> \u7684\u503C\uFF0C\u53D1\u9001\u7ED9\u63A5\u6536\u8005\uFF0C\u90A3\u4E48 <code>m</code> \u7684\u503C\u4E5F\u4F1A\u4E0D\u65AD\u53D8\u5316\uFF0C\u5E76\u53D1\u751F Padding \u6570\u636E\u9A8C\u8BC1\u5931\u8D25\u8FD4\u56DE\u3002\u9664\u975E <code>m</code> \u53D8\u6210 <code>0x00</code>, \u6B64\u65F6\u4E0D\u4F1A\u8FD4\u56DE Padding \u6570\u636E\u9A8C\u8BC1\u5931\u8D25\uFF0C\u800C\u4F1A\u8FD4\u56DE HMAC \u6570\u636E\u9A8C\u8BC1\u5931\u8D25\u3002\u5047\u8BBE\u6B64\u65F6\uFF0C<code>m</code> \u7684\u503C\u88AB\u6539\u4E3A\u4E86 <code>c</code>\uFF0C\u5982\u56FE\u3002</p><p><img src="'+t+'" alt="img"></p><p>\u53EF\u77E5 <code>a = c</code> \u5F02\u6216 <code>0x00</code>\u3002</p><p>\u7531\u4E8E <code>a = x</code> \u5F02\u6216 <code>m</code> \u5E76\u4E14 <code>m = x</code> \u5F02\u6216 <code>a</code>\u3002</p><p>\u6240\u4EE5 <code>m</code> \u4E5F\u5C31\u662F\u660E\u6587\uFF0C <code>m = x</code> \u5F02\u6216 <code>c</code> \u5F02\u6216 <code>0x00</code>\u3002\u5176\u4E2D\uFF0C <code>x</code> \u653B\u51FB\u8005\u5DF2\u77E5\uFF0C <code>c</code> \u901A\u8FC7\u4E0D\u540C\u7684\u8FD4\u56DE\u83B7\u77E5\u3002 \u4ECE\u800C\u7834\u89E3\u4E86\u660E\u6587\u4E2D\u7684\u6700\u540E\u4E00\u4E2A\u5B57\u8282\u3002</p><p>\u6709\u4E86 <code>m</code> \u7684\u503C\uFF0C\u4E5F\u540C\u65F6\u5F97\u5230\u4E86 <code>a</code> \u7684\u503C\uFF0C \u53EF\u4EE5\u901A\u8FC7\u4FEE\u6539 <code>x</code> \u7684\u503C\u63A7\u5236 <code>m</code>\uFF0C\u6BD4\u5982\u4FEE\u6539 <code>x</code> \u7684\u503C\u4F7F <code>m = 0x01 = x</code> \u5F02\u6216 <code>a</code>\u3002</p><p>\u5C06 <code>m</code> \u8BBE\u7F6E\u6210 <code>0x01</code> \u7684\u65F6\u5019\uFF0C\u904D\u5386\u5012\u6570\u7B2C\u4E8C\u4E2A\u5BC6\u6587\u5757\u7684\u5012\u6570\u7B2C\u4E8C\u4E2A\u5B57\u8282\uFF08\u6700\u591A256\u6B21\uFF09\uFF0C\u901A\u8FC7\u5224\u65AD\u662F\u5426\u901A\u8FC7 Padding \u9A8C\u8BC1\uFF0C\u5C31\u80FD\u540C\u7406\u6C42\u51FA\u53E6\u5916\u4E00\u4E2A\u5B57\u8282\u4E86\u3002</p><p>\u4EE5\u6B64\u7C7B\u63A8\u53EF\u4EE5\u89E3\u5BC6\u6574\u4E2A\u6570\u636E\u5305\u3002</p><h2 id="padding-oracle-\u653B\u51FB\u7684\u9632\u8303" tabindex="-1"><a class="header-anchor" href="#padding-oracle-\u653B\u51FB\u7684\u9632\u8303" aria-hidden="true">#</a> Padding Oracle \u653B\u51FB\u7684\u9632\u8303</h2><p>\u901A\u8FC7\u4E0A\u4E00\u8282\u53EF\u4EE5\u770B\u5230\u653B\u51FB\u8005\u662F\u901A\u8FC7 Padding \u9A8C\u8BC1\u8FD4\u56DE\u548C MAC \u9A8C\u8BC1\u8FD4\u56DE\u7ED3\u679C\u4E0D\u540C\u6765\u83B7\u5F97\u4FE1\u606F\u7684\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u5373\u4F7F Padding \u9A8C\u8BC1\u5931\u8D25\u548C MAC \u9A8C\u8BC1\u5931\u8D25\u90FD\u8FD4\u56DE\u540C\u6837\u7ED3\u679C\uFF0C\u653B\u51FB\u8005\u4E5F\u53EF\u4EE5\u901A\u8FC7\u54CD\u5E94\u65F6\u95F4\u7684\u4E0D\u540C\u901A\u8FC7 timing \u7684\u65B9\u5F0F\u83B7\u5F97\u4FE1\u606F\u3002</p><p>\u6240\u4EE5\u5728\u534F\u8BAE\u4E2D\u5C06\u9A8C\u8BC1\u5931\u8D25\u7684\u54CD\u5E94\u65F6\u95F4\u548C\u54CD\u5E94\u7ED3\u679C\u7EDF\u4E00\uFF0C\u4F7F\u653B\u51FB\u8005\u4E0D\u80FD\u533A\u5206\u80FD\u9632\u8303\u8FD9\u79CD\u653B\u51FB\u3002</p><p>\u800C\u5BF9\u4E8E SSL/TLS \u534F\u8BAE\u7684\u4F7F\u7528\u8005\uFF0C\u6211\u4EEC\u53EF\u4EE5\u54CD\u5E94\u53F7\u53EC\u5728\u81EA\u5DF1\u670D\u52A1\u5668\u7684\u52A0\u5BC6\u534F\u8BAE\u652F\u6301\u5217\u8868\u4E0A\u53BB\u6389 SSLv3.0\u3002</p>',61),s=[n];function h(S,g){return c(),o("div",null,s)}const m=d(r,[["render",h],["__file","HTTPS \u964D\u7EA7\u653B\u51FB.html.vue"]]);export{m as default};
