import{_ as e,o as i,c as a,a as l}from"./app.f0c02a19.js";const r="/assets/fenbu.82be210b.jpeg",c="/assets/RPC.553a923e.jpeg",n="/assets/SOA.0c880d9c.jpeg",s={},d=l('<h1 id="\u4F60\u5982\u4F55\u8003\u8651\u670D\u52A1\u5316" tabindex="-1"><a class="header-anchor" href="#\u4F60\u5982\u4F55\u8003\u8651\u670D\u52A1\u5316" aria-hidden="true">#</a> \u4F60\u5982\u4F55\u8003\u8651\u670D\u52A1\u5316</h1><h2 id="\u96C6\u4E2D\u5F0F\u4E0E\u5206\u5E03\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u96C6\u4E2D\u5F0F\u4E0E\u5206\u5E03\u5F0F" aria-hidden="true">#</a> \u96C6\u4E2D\u5F0F\u4E0E\u5206\u5E03\u5F0F</h2><p>\u8981\u8C08\u5FAE\u670D\u52A1\uFF0C\u90A3\u4E48\u5FC5\u987B\u5EFA\u7ACB\u5728\u5206\u5E03\u5F0F\u7684\u57FA\u7840\u4E0A\uFF0C\u5BF9\u4E8E\u4E00\u4E2A\u96C6\u4E2D\u5F0F\u7CFB\u7EDF\u4E5F\u65E0\u9700\u8C08\u5FAE\u670D\u52A1\u3002</p><p><img src="'+r+'" alt="img"></p><h3 id="\u96C6\u4E2D\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u96C6\u4E2D\u5F0F" aria-hidden="true">#</a> \u96C6\u4E2D\u5F0F</h3><p>\u96C6\u4E2D\u5F0F\u7CFB\u7EDF\u7528\u4E00\u53E5\u8BDD\u6982\u62EC\u5C31\u662F\uFF1A\u4E00\u4E2A\u4E3B\u673A\u5E26\u591A\u4E2A\u7EC8\u7AEF\u3002\u7EC8\u7AEF\u6CA1\u6709\u6570\u636E\u5904\u7406\u80FD\u529B\uFF0C\u4EC5\u8D1F\u8D23\u6570\u636E\u7684\u5F55\u5165\u548C\u8F93\u51FA\u3002\u800C\u8FD0\u7B97\u3001\u5B58\u50A8\u7B49\u5168\u90E8\u5728\u4E3B\u673A\u4E0A\u8FDB\u884C\u3002</p><p>\u96C6\u4E2D\u5F0F\u7CFB\u7EDF\u7684\u6700\u5927\u7684\u7279\u70B9\u5C31\u662F\u90E8\u7F72\u7ED3\u6784\u975E\u5E38\u7B80\u5355\uFF0C\u5E95\u5C42\u4E00\u822C\u91C7\u7528\u4ECEIBM\u3001HP\u7B49\u5382\u5546\u8D2D\u4E70\u5230\u7684\u6602\u8D35\u7684\u5927\u578B\u4E3B\u673A\u3002\u56E0\u6B64\u65E0\u9700\u8003\u8651\u5982\u4F55\u5BF9\u670D\u52A1\u8FDB\u884C\u591A\u8282\u70B9\u7684\u90E8\u7F72\uFF0C\u4E5F\u5C31\u4E0D\u7528\u8003\u8651\u5404\u8282\u70B9\u4E4B\u95F4\u7684\u5206\u5E03\u5F0F\u534F\u4F5C\u95EE\u9898\u3002\u4F46\u662F\uFF0C\u7531\u4E8E\u91C7\u7528\u5355\u673A\u90E8\u7F72\u3002\u5F88\u53EF\u80FD\u5E26\u6765\u7CFB\u7EDF\u5927\u800C\u590D\u6742\u3001\u96BE\u4E8E\u7EF4\u62A4\u3001\u53D1\u751F\u5355\u70B9\u6545\u969C\uFF08\u5355\u4E2A\u70B9\u53D1\u751F\u6545\u969C\u7684\u65F6\u5019\u4F1A\u6CE2\u53CA\u5230\u6574\u4E2A\u7CFB\u7EDF\u6216\u8005\u7F51\u7EDC\uFF0C\u4ECE\u800C\u5BFC\u81F4\u6574\u4E2A\u7CFB\u7EDF\u6216\u8005\u7F51\u7EDC\u7684\u762B\u75EA\uFF09\u3001\u6269\u5C55\u6027\u5DEE\u7B49\u95EE\u9898\u3002</p><h3 id="\u5206\u5E03\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F" aria-hidden="true">#</a> \u5206\u5E03\u5F0F</h3><p>\u5206\u5E03\u5F0F\u5C31\u662F\u4E00\u7FA4\u72EC\u7ACB\u8BA1\u7B97\u673A\u96C6\u5408\u5171\u540C\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1\uFF0C\u4F46\u662F\u5BF9\u4E8E\u7CFB\u7EDF\u7684\u7528\u6237\u6765\u8BF4\uFF0C\u5C31\u50CF\u662F\u4E00\u53F0\u8BA1\u7B97\u673A\u5728\u63D0\u4F9B\u670D\u52A1\u4E00\u6837\u3002\u5206\u5E03\u5F0F\u610F\u5473\u7740\u53EF\u4EE5\u91C7\u7528\u66F4\u591A\u7684\u666E\u901A\u8BA1\u7B97\u673A\uFF08\u76F8\u5BF9\u4E8E\u6602\u8D35\u7684\u5927\u578B\u673A\uFF09\u7EC4\u6210\u5206\u5E03\u5F0F\u96C6\u7FA4\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1\u3002\u8BA1\u7B97\u673A\u8D8A\u591A\uFF0CCPU\u3001\u5185\u5B58\u3001\u5B58\u50A8\u8D44\u6E90\u7B49\u4E5F\u5C31\u8D8A\u591A\uFF0C\u80FD\u591F\u5904\u7406\u7684\u5E76\u53D1\u8BBF\u95EE\u91CF\u4E5F\u5C31\u8D8A\u5927\u3002</p><p>\u62FF\u7535\u5546\u7F51\u7AD9\u6765\u8BF4\uFF0C\u6211\u4EEC\u4E00\u822C\u628A\u4E00\u4E2A\u7535\u5546\u7F51\u7AD9\u6A2A\u5411\u62C6\u5206\u6210\u5546\u54C1\u6A21\u5757\u3001\u8BA2\u5355\u6A21\u5757\u3001\u8D2D\u7269\u8F66\u6A21\u5757\u3001\u6D88\u606F\u6A21\u5757\u3001\u652F\u4ED8\u6A21\u5757\u7B49\u3002\u7136\u540E\u6211\u4EEC\u628A\u4E0D\u540C\u7684\u6A21\u5757\u90E8\u7F72\u5230\u4E0D\u540C\u7684\u673A\u5668\u4E0A\uFF0C\u5404\u4E2A\u6A21\u5757\u4E4B\u95F4\u901A\u8FC7\u8FDC\u7A0B\u670D\u52A1\u8C03\u7528(<code>RPC</code>)\u7B49\u65B9\u5F0F\u8FDB\u884C\u901A\u4FE1\u3002\u4EE5\u4E00\u4E2A\u5206\u5E03\u5F0F\u7684\u7CFB\u7EDF\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1\u3002</p><h2 id="\u670D\u52A1\u5316" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u5316" aria-hidden="true">#</a> \u670D\u52A1\u5316</h2><p>\u63D0\u5230\u5206\u5E03\u5F0F\uFF0C\u4E00\u4E2A\u4E0D\u5F97\u4E0D\u63D0\u7684\u8BCD\u5C31\u662F\u670D\u52A1\u5316\uFF0C\u670D\u52A1\u5316\u67B6\u6784\u4F7F\u642D\u5EFA\u5206\u5E03\u5F0F\u7CFB\u7EDF\u6210\u4E3A\u4E86\u53EF\u80FD\u3002</p><p>\u4F20\u7EDF\u7684\u8F6F\u4EF6\u5F00\u53D1\u9762\u4E34\u7740\u5F88\u591A\u7684\u95EE\u9898\uFF0C\u6BD4\u5982\uFF1A \u4EE3\u7801\u91CD\u590D\u7387\u9AD8\u3001\u4EE3\u7801\u5E9E\u5927\u96BE\u4EE5\u7EF4\u62A4\u3001\u65E0\u6CD5\u5FEB\u901F\u8FED\u4EE3\u3001\u6D4B\u8BD5\u6210\u672C\u9AD8\u3001\u53EF\u4F38\u7F29\u6027\u5DEE\u3001\u53EF\u9760\u6027\u5DEE\u3001\u6A21\u5757\u95F4\u9AD8\u5EA6\u4F9D\u8D56\u3002\u4E3A\u4E86\u89E3\u51B3\u4E0A\u9762\u8FD9\u4E9B\u95EE\u9898\uFF0C\u6211\u4EEC\u4E00\u822C\u91C7\u7528\u62C6\u5206\u3001\u89E3\u8026\u3001\u5206\u5C42\u3001\u72EC\u7ACB\u7B49\u65B9\u5F0F\u6765\u89E3\u51B3\u3002\u6709\u4E86\u670D\u52A1\u5316\u67B6\u6784\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\u3002</p><blockquote><p>\u670D\u52A1\u5316\u662F\u4E00\u79CD\u7C97\u7C92\u5EA6\u3001\u677E\u8026\u5408\u7684\u4EE5\u670D\u52A1\u4E3A\u4E2D\u5FC3\u7684\u67B6\u6784\uFF0C\u670D\u52A1\u4E4B\u95F4\u901A\u8FC7\u5B9A\u4E49\u660E\u786E\u7684\u534F\u8BAE\u548C\u63A5\u53E3\u8FDB\u884C\u901A\u4FE1\u3002</p></blockquote><p>\u8FD9\u91CC\u8BF4\u5230\u7684\u201C\u670D\u52A1\u201D\uFF0C\u672C\u8D28\u4E0A\u6765\u8BF4\uFF0C\u5C31\u662F\u6307\u201CRPC\u201D\u3002\u5355\u7EAF\u7684RPC\u529F\u80FD\u5B9E\u73B0\uFF0C\u5176\u5B9E\u5F88\u7B80\u5355\uFF0C\u65E0\u975E\u5C31\u662Fclient\u53D1\u8D77\u8C03\u7528\uFF0C\u4E2D\u95F4\u67D0\u4E2A\u7EC4\u4EF6\uFF08\u751A\u81F3\u5C31\u662Fclient\u672C\u8EAB\uFF09\u62E6\u622A\u8C03\u7528\u4FE1\u606F\uFF0C\u5E8F\u5217\u5316\u540E\u5C06\u4FE1\u606F\u4F20\u8F93\u5230server\u7AEF\uFF0Cserver\u7AEF\u6536\u5230\u8C03\u7528\u8BF7\u6C42\u540E\u53CD\u5E8F\u5217\u5316\uFF0C\u6839\u636E\u8BF7\u6C42\u8BE6\u7EC6\u53D1\u8D77\u5B9E\u9645\u8C03\u7528\u540E\u8FD4\u56DE\u54CD\u5E94\u4F20\u8F93\u56DE\u7ED9client\u7AEF\u3002\u8FD9\u6837\u7684RPC\u5F88\u5E38\u89C1\uFF0C\u6BD4\u5982\u5E38\u89C1\u7684\u5B58\u50A8\u8FC7\u7A0B\u8C03\u7528\u5C31\u662F\u4E00\u4F8B\u3002\u4F46\u662F\u5728\u4E00\u4E2A\u590D\u6742\u7684\u4E1A\u52A1\u73AF\u5883\uFF0C\u5982\u4F55\u7BA1\u7406\u548C\u534F\u540C\u8FD9\u4E9B\u5927\u91CF\u7684RPC\u624D\u662F\u6700\u9EBB\u70E6\u7684\u4E8B\u60C5\u3002\u6240\u4EE5\uFF0C\u4E00\u822C\u63D0\u5230\u7684\u201C\u670D\u52A1\u5316\u201D\u66F4\u591A\u6307\u7684\u662F\u5BF9RPC\u7684\u7BA1\u7406\u3002\u670D\u52A1\u5316\u4E00\u822C\u5173\u6CE8\u670D\u52A1\u6CE8\u518C\uFF0C\u670D\u52A1\u534F\u8C03\uFF0C\u670D\u52A1\u53EF\u7528\u6027\uFF0C\u670D\u52A1\u901A\u8BAF\u534F\u8BAE\u548C\u5185\u5BB9\u4EA4\u6362\u7B49\u3002</p><p><img src="'+c+'" alt="img"></p><h2 id="\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784" aria-hidden="true">#</a> \u9762\u5411\u670D\u52A1\u7684\u67B6\u6784</h2><p>\u9762\u5411\u670D\u52A1\u67B6\u6784\uFF08Service-Oriented Architecture\uFF0C<code>SOA</code>\uFF09\u53C8\u79F0\u201C\u9762\u5411\u670D\u52A1\u7684\u4F53\u7CFB\u7ED3\u6784\u201D\uFF0C\u662FGartner\u4E8E2O\u4E16\u7EAA9O\u5E74\u4EE3\u4E2D\u671F\u63D0\u51FA\u7684\u9762\u5411\u670D\u52A1\u67B6\u6784\u7684\u6982\u5FF5\u3002</p><blockquote><p>\u9762\u5411\u670D\u52A1\u67B6\u6784\uFF0C\u4ECE\u8BED\u4E49\u4E0A\u8BF4\uFF0C\u5B83\u4E0E\u9762\u5411\u8FC7\u7A0B\u3001\u9762\u5411\u5BF9\u8C61\u3001\u9762\u5411\u7EC4\u4EF6\u4E00\u6837\uFF0C\u662F\u4E00\u79CD\u8F6F\u4EF6\u7EC4\u5EFA\u53CA\u5F00\u53D1\u7684\u65B9\u5F0F\u3002\u4E0E\u4EE5\u5F80\u7684\u8F6F\u4EF6\u5F00\u53D1\u3001\u67B6\u6784\u6A21\u5F0F\u4E00\u6837\uFF0CSOA \u53EA\u662F\u4E00\u79CD\u4F53\u7CFB\u3001\u4E00\u79CD\u601D\u60F3\uFF0C\u800C\u4E0D\u662F\u67D0\u79CD\u5177\u4F53\u7684\u8F6F\u4EF6\u4EA7\u54C1\u3002</p></blockquote><p><img src="'+n+`" alt="img"></p><p>\u8FD9\u91CC\uFF0C\u6211\u4EEC\u901A\u8FC7\u4E00\u4E2A\u4F8B\u5B50\u6765\u89E3\u91CA\u4E00\u4E0B\u5230\u5E95\u4EC0\u4E48\u662F SOA\uFF1F\u5982\u4F55\u505A\u5230 SOA\uFF1F</p><h3 id="\u4EC0\u4E48\u662F-soa" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F-soa" aria-hidden="true">#</a> \u4EC0\u4E48\u662F SOA</h3><p>SOA \u4E5F\u53EF\u4EE5\u8BF4\u662F\u4E00\u79CD\u662F\u8BBE\u8BA1\u539F\u5219\uFF08\u6A21\u5F0F\uFF09\uFF0C\u90A3\u4E48\u5B83\u5305\u542B\u54EA\u4E9B\u5185\u5BB9\u5462\uFF1F\u4E8B\u5B9E\u4E0A\uFF0C\u8FD9\u65B9\u9762\u5E76\u6CA1\u6709\u6700\u6807\u51C6\u7684\u7B54\u6848\uFF0C\u591A\u6570\u662F\u9075\u4ECE\u8457\u540D SOA \u4E13\u5BB6 Thomas Erl \u7684\u5F52\u7EB3\uFF1A</p><ul><li>\u6807\u51C6\u5316\u7684\u670D\u52A1\u5951\u7EA6 Standardized service contract</li><li>\u670D\u52A1\u7684\u677E\u8026\u5408 Service loose coupling</li><li>\u670D\u52A1\u7684\u62BD\u8C61 Service abstraction</li><li>\u670D\u52A1\u7684\u53EF\u91CD\u7528\u6027 Service reusability</li><li>\u670D\u52A1\u7684\u81EA\u6CBB\u6027 Service autonomy</li><li>\u670D\u52A1\u7684\u65E0\u72B6\u6001\u6027 Service statelessness</li><li>\u670D\u52A1\u7684\u53EF\u53D1\u73B0\u6027 Service discoverability</li><li>\u670D\u52A1\u7684\u53EF\u7EC4\u5408\u6027 Service composability</li></ul><p>\u8FD9\u4E9B\u539F\u5219\u603B\u7684\u6765\u8BF4\u8981\u8FBE\u5230\u7684\u76EE\u7684\u662F\uFF1A\u63D0\u9AD8\u8F6F\u4EF6\u7684\u91CD\u7528\u6027\uFF0C\u51CF\u5C11\u5F00\u53D1\u548C\u7EF4\u62A4\u7684\u6210\u672C\uFF0C\u6700\u7EC8\u589E\u52A0\u4E00\u4E2A\u516C\u53F8\u4E1A\u52A1\u7684\u654F\u6377\u5EA6\u3002\u65E2\u7136\u662F\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784\uFF0C\u90A3\u4E48\u6211\u4EEC\u5C31\u5148\u6765\u5B9A\u4E49\u4E00\u4E2A\u670D\u52A1\uFF0C</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>public interface Echo {
    String echo(String text);
}

public class EchoImpl implements Echo {
    public String echo(String text) {
        return text;
    }
}
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\u4E0A\u9762\u8FD9\u6BB5\u4EE3\u7801\u76F8\u4FE1\u6709\u8FC7 JavaWeb \u5F00\u53D1\u7ECF\u9A8C\u7684\u4EBA\u90FD\u4E0D\u4F1A\u964C\u751F\u3002\u5C31\u662F\u5B9A\u4E49\u4E86\u4E00\u4E2A\u670D\u52A1\u7684\u63A5\u53E3\u548C\u5B9E\u73B0\u3002</p><p>\u90A3\u4E48\uFF0C\u5B9A\u4E49\u4E86\u670D\u52A1\uFF0C\u6211\u4EEC\u5C31\u505A\u5230\u4E86 SOA \u4E86\u4E48\uFF1F</p><p>\u6211\u4EEC\u7528 Thomas Erl \u5B9A\u4E49\u7684\u539F\u5219\u6765\u5BF9\u6BD4\u4E00\u4E0B\uFF0C\u7528\u677E\u8026\u5408\u548C\u53EF\u91CD\u7528\u8FD9\u51E0\u4E2A\u539F\u5219\u6765\u5C1D\u8BD5\u5206\u6790\u4E00\u4E0B\u4E0A\u9762 Echo \u793A\u4F8B\uFF1A</p><ul><li>Echo \u7684\u670D\u52A1\u5951\u7EA6\u662F\u7528 Java \u63A5\u53E3\u5B9A\u4E49\uFF0C\u800C\u4E0D\u662F\u4E00\u79CD\u4E0E\u5E73\u53F0\u548C\u8BED\u8A00\u65E0\u5173\u7684\u6807\u51C6\u5316\u534F\u8BAE\uFF0C\u5982 WSDL\uFF0CCORBA IDL\u3002\u5F53\u7136\u53EF\u4EE5\u62AC\u6760\uFF0CJava \u4E5F\u662F\u884C\u4E1A\u6807\u51C6\uFF0C\u751A\u81F3\u5168\u56FD\u7259\u9632\u7EC4\u4E00\u81F4\u8BA4\u5B9A\u7684\u4E1C\u897F\u4E5F\u662F\u884C\u4E1A\u6807\u51C6\u3002</li><li>Java \u63A5\u53E3\u5927\u5927\u52A0\u91CD\u4E86\u4E0E Service \u5BA2\u6237\u7AEF\u7684\u8026\u5408\u5EA6\uFF0C\u5373\u8981\u6C42\u5BA2\u6237\u7AEF\u5FC5\u987B\u4E5F\u662F Java\uFF0C\u6216\u8005 JVM \u4E0A\u7684\u52A8\u6001\u8BED\u8A00\uFF08\u5982Groovy\u3001Jython\uFF09\u7B49\u7B49\u2026\u2026</li><li>\u540C\u65F6\uFF0CEcho \u662F\u4E00\u4E2A Java \u7684\u672C\u5730\u63A5\u53E3\uFF0C\u5C31\u8981\u6C42\u8C03\u7528\u8005\u6700\u597D\u5728\u540C\u4E00\u4E2A JVM \u8FDB\u7A0B\u4E4B\u5185\u2026\u2026</li><li>Echo \u7684\u4E1A\u52A1\u903B\u8F91\u867D\u7136\u7B80\u5355\u72EC\u7ACB\uFF0C\u4F46\u4EE5\u4E0A\u6280\u672F\u65B9\u9762\u7684\u5C40\u9650\u5C31\u5BFC\u81F4\u5B83\u65E0\u6CD5\u4EE5\u540E\u5728\u5176\u4ED6\u573A\u5408\u88AB\u8F7B\u6613\u91CD\u7528\uFF0C\u6BD4\u5982\u5206\u5E03\u5F0F\u73AF\u5883\uFF0C\u5F02\u6784\u5E73\u53F0\u7B49\u7B49 ESB \u662F SCA \u601D\u60F3\u5B9E\u73B0\u7684\u57FA\u7840\u8BBE\u65BD\u3002ESB \u4E3B\u8981\u4F5C\u7528\u662F\u96C6\u4E2D\u6CE8\u518C\u53D1\u5E03\u670D\u52A1\uFF0C\u4E3A\u670D\u52A1\u4E0E\u4F20\u8F93\u534F\u8BAE\u4E4B\u95F4\u89E3\u8026\u3002\u5E76\u4E0D\u662F\u6240\u6709\u7684 SOA \u67B6\u6784\u90FD\u9700\u8981 ESB\uFF0CESB \u662F SCA \u7279\u6709\u7684\u3002\u5F53\u7136\u4EFB\u4F55\u7B26\u5408 ESB \u7279\u5F81\u7684\u89E3\u51B3\u65B9\u5F0F\u90FD\u53EF\u4EE5\u79F0\u4E4B\u4E3A ESB\uFF0C\u4E5F\u4E0D\u4EC5\u4EC5\u662F SCA \u5185\u90E8\u7684\u3002</li></ul><p>\u56E0\u6B64\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BA4\u4E3A Echo \u5E76\u4E0D\u592A\u7B26\u5408 SOA \u7684\u57FA\u672C\u8BBE\u8BA1\u539F\u5219\u3002</p><h3 id="\u5B9E\u73B0-soa" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u73B0-soa" aria-hidden="true">#</a> \u5B9E\u73B0 SOA</h3><p>\u4FEE\u6539\u4E00\u4E0B\u4E0A\u9762\u7684 Echo\uFF0C\u6DFB\u52A0 Java EE \u7684 <code>@WebServices</code> \u6CE8\u89E3</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>@WebServices
public class EchoImpl implements Echo {
    public String echo(String text) {
        return text;
    }
}
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\u73B0\u5728\u5C06 Echo \u53D1\u5E03\u4E3A Java WebServices\uFF0C\u5E76\u7531\u5E95\u5C42\u6846\u67B6\u81EA\u52A8\u751F\u6210 WSDL \u6765\u4F5C\u4E3A\u6807\u51C6\u5316\u7684\u670D\u52A1\u5951\u7EA6\uFF0C\u8FD9\u6837\u5C31\u80FD\u4E0E\u8FDC\u7A0B\u7684\u5404\u79CD\u8BED\u8A00\u548C\u5E73\u53F0\u4E92\u64CD\u4F5C\u4E86\uFF0C\u8F83\u597D\u7684\u89E3\u51B3\u4E86\u4E0A\u9762\u63D0\u5230\u7684\u677E\u8026\u5408\u548C\u53EF\u91CD\u7528\u7684\u95EE\u9898\u3002\u6309\u7167\u4E00\u822C\u7684\u7406\u89E3\uFF0CEcho \u4F3C\u4E4E\u5C31\u6210\u4E3A\u6BD4\u8F83\u7406\u60F3\u7684 SOA service\u4E86\u3002</p><p>\u4F7F\u7528 WebServices \u53EA\u662F\u4E00\u79CD\u76F8\u5BF9\u7B80\u5355\u7684\u65B9\u6848\uFF0CSOA \u7684\u6700\u5E38\u89C1\u7684\u89E3\u51B3\u65B9\u6848\u662F SCA\uFF0C\u5176\u6B21\u8FD8\u6709 JBI\uFF0CBPEL \u7B49\u3002ESB \u662F SCA \u601D\u60F3\u5B9E\u73B0\u7684\u57FA\u7840\u8BBE\u65BD\u3002ESB \u4E3B\u8981\u4F5C\u7528\u662F\u96C6\u4E2D\u6CE8\u518C\u53D1\u5E03\u670D\u52A1\uFF0C\u4E3A\u670D\u52A1\u4E0E\u4F20\u8F93\u534F\u8BAE\u4E4B\u95F4\u89E3\u8026\u3002\u5173\u4E8E SCA \u548C ESB \u5E76\u4E0D\u662F\u672C\u6587\u7684\u91CD\u70B9\uFF0C\u611F\u5174\u8DA3\u7684\u670B\u53CB\u53EF\u4EE5\u4ECE\u7F51\u7EDC\u4E0A\u83B7\u53D6\u66F4\u591A\u8D44\u6599\u3002(\u53EF\u4EE5\u4ECE\u4E0A\u56FE\u4E2D\u770B\u5230 ESB \u5728\u6574\u4E2A SOA \u67B6\u6784\u4E2D\u6240\u626E\u6F14\u7684\u89D2\u8272)</p><h2 id="\u9762\u5411\u5BF9\u8C61\u548C\u9762\u5411\u670D\u52A1\u7684\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#\u9762\u5411\u5BF9\u8C61\u548C\u9762\u5411\u670D\u52A1\u7684\u5BF9\u6BD4" aria-hidden="true">#</a> \u9762\u5411\u5BF9\u8C61\u548C\u9762\u5411\u670D\u52A1\u7684\u5BF9\u6BD4</h2><p>\u9762\u5411\u5BF9\u8C61\uFF08<code>OO</code>\uFF09\u548C\u9762\u5411\u670D\u52A1\uFF08<code>SO</code>\uFF09\u5728\u57FA\u7840\u7406\u5FF5\u4E0A\u6709\u5927\u91CF\u5171\u901A\u4E4B\u5904\uFF0C\u6BD4\u5982\u90FD\u5C3D\u53EF\u80FD\u8FFD\u6C42\u62BD\u8C61\u3001\u5C01\u88C5\u548C\u4F4E\u8026\u5408\u3002</p><p>\u4F46 SO \u76F8\u5BF9\u4E8E OO\uFF0C\u53C8\u6709\u975E\u5E38\u4E0D\u540C\u7684\u5178\u578B\u5E94\u7528\u573A\u666F\uFF0C\u6BD4\u5982\uFF1A</p><ul><li>\u591A\u6570 OO \u63A5\u53E3\uFF08interface\uFF09\u90FD\u53EA\u88AB\u6709\u9650\u7684\u4EBA\u4F7F\u7528\uFF08\u6BD4\u5982\u56E2\u961F\u548C\u90E8\u95E8\u5185\uFF09\uFF0C\u800C SO \u63A5\u53E3\uFF08\u6216\u8005\u53EB\u5951\u7EA6\uFF09\u4E00\u822C\u6765\u8BF4\u90FD\u4E0D\u5E94\u8BE5\u5BF9\u4F7F\u7528\u8005\u7684\u8303\u56F4\u4F5C\u51FA\u592A\u591A\u7684\u9650\u5B9A\u548C\u5047\u8BBE\uFF08\u53EF\u4EE5\u662F\u4E0D\u540C\u90E8\u95E8\uFF0C\u4E0D\u540C\u4F01\u4E1A\uFF0C\u4E0D\u540C\u56FD\u5BB6\uFF09\u3002\u8FD8\u8BB0\u5F97\u8D1D\u4F50\u65AF\u539F\u5219\u5417\uFF1F\u201C\u56E2\u961F\u5FC5\u987B\u505A\u597D\u89C4\u5212\u4E0E\u8BBE\u8BA1\uFF0C\u4EE5\u4FBF\u672A\u6765\u628A\u63A5\u53E3\u5F00\u653E\u7ED9\u5168\u4E16\u754C\u7684\u7A0B\u5E8F\u5458\uFF0C\u6CA1\u6709\u4EFB\u4F55\u4F8B\u5916\u201D\u3002</li><li>\u591A\u6570 OO \u63A5\u53E3\u90FD\u53EA\u5728\u8FDB\u7A0B\u5185\u88AB\u8BBF\u95EE\uFF0C\u800C SO \u63A5\u53E3\u901A\u5E38\u90FD\u662F\u88AB\u8FDC\u7A0B\u8C03\u7528\u3002</li></ul><p>\u7B80\u5355\u8BB2\uFF0C\u5C31\u662F SO \u63A5\u53E3\u4F7F\u7528\u8303\u56F4\u6BD4\u4E00\u822C OO \u63A5\u53E3\u53EF\u80FD\u5E7F\u6CDB\u5F97\u591A\u3002\u6211\u4EEC\u7528\u7F51\u7AD9\u6253\u4E2A\u6BD4\u65B9\uFF1A\u4E00\u4E2A\u5927\u578B\u7F51\u7AD9\u7684 web \u754C\u9762\u5C31\u662F\u5B83\u6574\u4E2A\u7CFB\u7EDF\u5165\u53E3\u70B9\u548C\u8FB9\u754C\uFF0C\u53EF\u80FD\u8981\u9762\u5BF9\u5168\u4E16\u754C\u7684\u8BBF\u95EE\u8005\uFF08\u6240\u4EE5\u7ECF\u5E38\u4F1A\u505A\u56FD\u9645\u5316\u4E4B\u7C7B\u7684\u5DE5\u4F5C\uFF09\uFF0C\u800C\u7CFB\u7EDF\u5185\u90E8\u4F20\u7EDF\u7684 OO \u63A5\u53E3\u548C\u7A0B\u5E8F\u5219\u88AB\u9690\u85CF\u5728 web \u754C\u9762\u4E4B\u540E\uFF0C\u53EA\u88AB\u5185\u90E8\u8F83\u5C0F\u8303\u56F4\u4F7F\u7528\u3002\u800C\u7406\u60F3\u7684 SO \u63A5\u53E3\u548C web \u754C\u9762\u4E00\u6837\uFF0C\u4E5F\u662F\u53D8\u6210\u7CFB\u7EDF\u5165\u53E3\u548C\u8FB9\u754C\uFF0C\u53EF\u80FD\u8981\u5BF9\u5168\u4E16\u754C\u5F00\u53D1\u8005\u5F00\u653E\uFF0C\u56E0\u6B64 SO \u5728\u8BBE\u8BA1\u5F00\u53D1\u4E4B\u4E2D\u4E0E OO \u76F8\u6BD4\u5176\u5B9E\u4F1A\u6709\u5F88\u591A\u4E0D\u540C\u3002</p><h2 id="\u5FAE\u670D\u52A1\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u5FAE\u670D\u52A1\u67B6\u6784" aria-hidden="true">#</a> \u5FAE\u670D\u52A1\u67B6\u6784</h2><p>\u5FAE\u670D\u52A1\u67B6\u6784(<code>MicroService</code>)\u662F\u4E00\u79CD\u670D\u52A1\u5316\u67B6\u6784\u98CE\u683C\uFF0C\u901A\u8FC7\u5C06\u529F\u80FD\u5206\u6563\u5230\u5404\u4E2A\u79BB\u6563\u7684\u670D\u52A1\u4E2D\u4EE5\u5B9E\u73B0\u5BF9\u89E3\u51B3\u65B9\u6848\u7684\u89E3\u8026\u3002\u5FAE\u670D\u52A1\u67B6\u6784\u5F3A\u8C03\u7684\u7B2C\u4E00\u4E2A\u91CD\u70B9\u5C31\u662F\u4E1A\u52A1\u7CFB\u7EDF\u9700\u8981\u5F7B\u5E95\u7684\u7EC4\u4EF6\u5316\u548C\u670D\u52A1\u5316\uFF08\u8FD9\u4E5F\u662F\u6211\u4EEC\u4E3A\u4EC0\u4E48\u8981\u5148\u4ECB\u7ECD\u7EC4\u4EF6\u5316\u548C\u670D\u52A1\u5316\u7684\u539F\u56E0\uFF09\u3002\u5FAE\u670D\u52A1\u7684\u8BDE\u751F\u5E76\u975E\u5076\u7136\u3002\u5B83\u662F\u4E92\u8054\u7F51\u9AD8\u901F\u53D1\u5C55\uFF0C\u654F\u6377\u3001\u7CBE\u76CA\u3001\u6301\u7EED\u4EA4\u4ED8\u65B9\u6CD5\u8BBA\u7684\u6DF1\u5165\u4EBA\u5FC3\uFF0C\u865A\u62DF\u5316\u6280\u672F\u4E0E DevOps \u6587\u5316\u7684\u5FEB\u901F\u53D1\u5C55\u4EE5\u53CA\u4F20\u7EDF\u5355\u5757\u67B6\u6784\u65E0\u6CD5\u9002\u5E94\u5FEB\u901F\u53D8\u5316\u7B49\u591A\u91CD\u56E0\u7D20\u7684\u63A8\u52A8\u4E0B\u6240\u8BDE\u751F\u7684\u4EA7\u7269\u3002</p><p>\u5FAE\u670D\u52A1\u7684\u6D41\u884C\uFF0CMartin \u529F\u4E0D\u53EF\u6CA1\uFF0C\u5148\u770B\u770B\u4ED6\u662F\u5982\u4F55\u5B9A\u4E49\u5FAE\u670D\u52A1\u7684\uFF1A</p><ul><li>\u4E00\u4E9B\u5217\u7684\u72EC\u7ACB\u7684\u670D\u52A1\u5171\u540C\u7EC4\u6210\u7CFB\u7EDF</li><li>\u5355\u72EC\u90E8\u7F72\uFF0C\u8DD1\u5728\u81EA\u5DF1\u7684\u8FDB\u7A0B\u91CC</li><li>\u6BCF\u4E2A\u670D\u52A1\u4E3A\u72EC\u7ACB\u7684\u4E1A\u52A1\u5F00\u53D1</li><li>\u5206\u5E03\u5F0F\u7684\u7BA1\u7406</li></ul><p>Martin \u81EA\u5DF1\u4E5F\u8BF4\u4E86\uFF0C\u6BCF\u4E2A\u4EBA\u5BF9\u5FAE\u670D\u52A1\u90FD\u53EF\u4EE5\u6709\u81EA\u5DF1\u7684\u7406\u89E3\uFF0C\u4E0D\u8FC7\u5927\u6982\u7684\u6807\u51C6\u8FD8\u662F\u6709\u4E00\u4E9B\u7684\u3002</p><ul><li>\u5206\u5E03\u5F0F\u670D\u52A1\u7EC4\u6210\u7684\u7CFB\u7EDF</li><li>\u6309\u7167\u4E1A\u52A1\u800C\u4E0D\u662F\u6280\u672F\u6765\u5212\u5206\u7EC4\u7EC7</li><li>\u505A\u6709\u751F\u547D\u7684\u4EA7\u54C1\u800C\u4E0D\u662F\u9879\u76EE</li><li>Smart endpoints and dumb pipes\uFF08\u6211\u7684\u7406\u89E3\u662F\u5F3A\u670D\u52A1\u4E2A\u4F53\u548C\u5F31\u901A\u4FE1\uFF09</li><li>\u81EA\u52A8\u5316\u8FD0\u7EF4\uFF08DevOps\uFF09</li><li>\u5BB9\u9519</li><li>\u5FEB\u901F\u6F14\u5316</li></ul><h2 id="soa-\u548C\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#soa-\u548C\u5FAE\u670D\u52A1" aria-hidden="true">#</a> SOA \u548C\u5FAE\u670D\u52A1</h2><p>\u770B\u4E86 SOA \u548C\u5FAE\u670D\u52A1\uFF0C\u5F88\u591A\u4EBA\u4F1A\u8BA4\u4E3A\u8FD9\u4E0D\u5C31\u662F\u4E00\u56DE\u4E8B\u513F\u4E48\u3002\u5176\u5B9E SOA \u548C\u5FAE\u670D\u52A1\u5C31\u662F\u5DEE\u4E0D\u591A\u7684\u3002</p><ul><li>SOA \u5173\u6CE8\u7684\u662F\u670D\u52A1\u91CD\u7528\uFF0C\u5FAE\u670D\u52A1\u5728\u5173\u6CE8\u670D\u52A1\u91CD\u7528\u7684\u540C\u65F6\uFF0C\u4E5F\u540C\u65F6\u5173\u6CE8\u5FEB\u901F\u4EA4\u4ED8\uFF1B</li><li>\u5FAE\u670D\u52A1\u4E0D\u518D\u5F3A\u8C03\u4F20\u7EDF SOA \u67B6\u6784\u91CC\u9762\u6BD4\u8F83\u91CD\u7684 ESB \u4F01\u4E1A\u670D\u52A1\u603B\u7EBF\u3002\u5FAE\u670D\u52A1\u628A\u6240\u6709\u7684\u201C\u601D\u8003\u201D\u903B\u8F91\u5305\u62EC\u8DEF\u7531\u3001\u6D88\u606F\u89E3\u6790\u7B49\u653E\u5728\u670D\u52A1\u5185\u90E8\uFF0C\u53BB\u6389\u4E00\u4E2A\u5927\u4E00\u7EDF\u7684 ESB\uFF0C\u670D\u52A1\u95F4\u8F7B\u901A\u4FE1\uFF0C\u662F\u6BD4 SOA \u66F4\u5F7B\u5E95\u7684\u62C6\u5206\u3002</li></ul>`,50),t=[d];function o(h,p){return i(),a("div",null,t)}const u=e(s,[["render",o],["__file","\u4F60\u5982\u4F55\u8003\u8651\u670D\u52A1\u5316.html.vue"]]);export{u as default};
