import{_ as o,o as i,c as e,a as t}from"./app.f0c02a19.js";const a={},r=t('<h1 id="\u5206\u5E03\u5F0F\u4E8B\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u4E8B\u52A1" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u4E8B\u52A1</h1><h2 id="\u5206\u5E03\u5F0F\u4E00\u81F4\u6027" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u4E00\u81F4\u6027" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u4E00\u81F4\u6027</h2><p>\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u4E3A\u4E86\u4FDD\u8BC1\u6570\u636E\u7684\u9AD8\u53EF\u7528\uFF0C\u901A\u5E38\uFF0C\u6211\u4EEC\u4F1A\u5C06\u6570\u636E\u4FDD\u7559\u591A\u4E2A\u526F\u672C(replica)\uFF0C\u8FD9\u4E9B\u526F\u672C\u4F1A\u653E\u7F6E\u5728\u4E0D\u540C\u7684\u7269\u7406\u7684\u673A\u5668\u4E0A\u3002\u4E3A\u4E86\u5BF9\u7528\u6237\u63D0\u4F9B\u6B63\u786E\u7684 CRUD \u7B49\u8BED\u4E49\uFF0C\u6211\u4EEC\u9700\u8981\u4FDD\u8BC1\u8FD9\u4E9B\u653E\u7F6E\u5728\u4E0D\u540C\u7269\u7406\u673A\u5668\u4E0A\u7684\u526F\u672C\u662F\u4E00\u81F4\u7684\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u79CD\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u95EE\u9898\uFF0C\u524D\u4EBA\u5728\u6027\u80FD\u548C\u6570\u636E\u4E00\u81F4\u6027\u7684\u53CD\u53CD\u590D\u590D\u6743\u8861\u8FC7\u7A0B\u4E2D\u603B\u7ED3\u4E86\u8BB8\u591A\u5178\u578B\u7684\u534F\u8BAE\u548C\u7B97\u6CD5\u3002\u5176\u4E2D\u6BD4\u8F83\u8457\u540D\u7684\u6709<strong>\u4E8C\u9636\u63D0\u4EA4\u534F\u8BAE\uFF08Two Phase Commitment Protocol\uFF09</strong>\u3001<strong>\u4E09\u9636\u63D0\u4EA4\u534F\u8BAE\uFF08Three Phase Commitment Protocol\uFF09</strong> \u548C <strong>Paxos \u7B97\u6CD5</strong>\u3002</p><h2 id="\u5206\u5E03\u5F0F\u4E8B\u52A1-1" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u4E8B\u52A1-1" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u4E8B\u52A1</h2><blockquote><p>\u5206\u5E03\u5F0F\u4E8B\u52A1\u662F\u6307\u4F1A\u6D89\u53CA\u5230\u64CD\u4F5C\u591A\u4E2A\u6570\u636E\u5E93\u7684\u4E8B\u52A1\u3002\u5176\u5B9E\u5C31\u662F\u5C06\u5BF9\u540C\u4E00\u5E93\u4E8B\u52A1\u7684\u6982\u5FF5\u6269\u5927\u5230\u4E86\u5BF9\u591A\u4E2A\u5E93\u7684\u4E8B\u52A1\u3002\u76EE\u7684\u662F\u4E3A\u4E86\u4FDD\u8BC1\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u7684\u6570\u636E\u4E00\u81F4\u6027\u3002\u5206\u5E03\u5F0F\u4E8B\u52A1\u5904\u7406\u7684\u5173\u952E\u662F\u5FC5\u987B\u6709\u4E00\u79CD\u65B9\u6CD5\u53EF\u4EE5\u77E5\u9053\u4E8B\u52A1\u5728\u4EFB\u4F55\u5730\u65B9\u6240\u505A\u7684\u6240\u6709\u52A8\u4F5C\uFF0C\u63D0\u4EA4\u6216\u56DE\u6EDA\u4E8B\u52A1\u7684\u51B3\u5B9A\u5FC5\u987B\u4EA7\u751F\u7EDF\u4E00\u7684\u7ED3\u679C\uFF08\u5168\u90E8\u63D0\u4EA4\u6216\u5168\u90E8\u56DE\u6EDA\uFF09</p></blockquote><p>\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u5404\u4E2A\u8282\u70B9\u4E4B\u95F4\u5728\u7269\u7406\u4E0A\u76F8\u4E92\u72EC\u7ACB\uFF0C\u901A\u8FC7\u7F51\u7EDC\u8FDB\u884C\u6C9F\u901A\u548C\u534F\u8C03\u3002\u7531\u4E8E\u5B58\u5728\u4E8B\u52A1\u673A\u5236\uFF0C\u53EF\u4EE5\u4FDD\u8BC1\u6BCF\u4E2A\u72EC\u7ACB\u8282\u70B9\u4E0A\u7684\u6570\u636E\u64CD\u4F5C\u53EF\u4EE5\u6EE1\u8DB3 ACID\u3002\u4F46\u662F\uFF0C\u76F8\u4E92\u72EC\u7ACB\u7684\u8282\u70B9\u4E4B\u95F4\u65E0\u6CD5\u51C6\u786E\u7684\u77E5\u9053\u5176\u4ED6\u8282\u70B9\u4E2D\u7684\u4E8B\u52A1\u6267\u884C\u60C5\u51B5\u3002\u6240\u4EE5\u4ECE\u7406\u8BBA\u4E0A\u8BB2\uFF0C\u4E24\u53F0\u673A\u5668\u7406\u8BBA\u4E0A\u65E0\u6CD5\u8FBE\u5230\u4E00\u81F4\u7684\u72B6\u6001\u3002\u5982\u679C\u60F3\u8BA9\u5206\u5E03\u5F0F\u90E8\u7F72\u7684\u591A\u53F0\u673A\u5668\u4E2D\u7684\u6570\u636E\u4FDD\u6301\u4E00\u81F4\u6027\uFF0C\u90A3\u4E48\u5C31\u8981\u4FDD\u8BC1\u5728\u6240\u6709\u8282\u70B9\u7684\u6570\u636E\u5199\u64CD\u4F5C\uFF0C\u8981\u4E0D\u5168\u90E8\u90FD\u6267\u884C\uFF0C\u8981\u4E48\u5168\u90E8\u7684\u90FD\u4E0D\u6267\u884C\u3002\u4F46\u662F\uFF0C\u4E00\u53F0\u673A\u5668\u5728\u6267\u884C\u672C\u5730\u4E8B\u52A1\u7684\u65F6\u5019\u65E0\u6CD5\u77E5\u9053\u5176\u4ED6\u673A\u5668\u4E2D\u7684\u672C\u5730\u4E8B\u52A1\u7684\u6267\u884C\u7ED3\u679C\u3002\u6240\u4EE5\u4ED6\u4E5F\u5C31\u4E0D\u77E5\u9053\u672C\u6B21\u4E8B\u52A1\u5230\u5E95\u5E94\u8BE5 commit \u8FD8\u662F rollback\u3002\u6240\u4EE5\uFF0C\u5E38\u89C4\u7684\u89E3\u51B3\u529E\u6CD5\u5C31\u662F\u5F15\u5165\u4E00\u4E2A\u201C\u534F\u8C03\u8005\u201D\u7684\u7EC4\u4EF6\u6765\u7EDF\u4E00\u8C03\u5EA6\u6240\u6709\u5206\u5E03\u5F0F\u8282\u70B9\u7684\u6267\u884C\u3002</p><h2 id="xa-\u89C4\u8303" tabindex="-1"><a class="header-anchor" href="#xa-\u89C4\u8303" aria-hidden="true">#</a> XA \u89C4\u8303</h2><p>X/Open \u7EC4\u7EC7\uFF08\u5373\u73B0\u5728\u7684 Open Group \uFF09\u5B9A\u4E49\u4E86\u5206\u5E03\u5F0F\u4E8B\u52A1\u5904\u7406\u6A21\u578B\u3002 X/Open DTP \u6A21\u578B\uFF08 1994 \uFF09\u5305\u62EC\u5E94\u7528\u7A0B\u5E8F\uFF08 AP \uFF09\u3001\u4E8B\u52A1\u7BA1\u7406\u5668\uFF08 TM \uFF09\u3001\u8D44\u6E90\u7BA1\u7406\u5668\uFF08 RM \uFF09\u3001\u901A\u4FE1\u8D44\u6E90\u7BA1\u7406\u5668\uFF08 CRM \uFF09\u56DB\u90E8\u5206\u3002\u4E00\u822C\uFF0C\u5E38\u89C1\u7684\u4E8B\u52A1\u7BA1\u7406\u5668\uFF08 TM \uFF09\u662F\u4EA4\u6613\u4E2D\u95F4\u4EF6\uFF0C\u5E38\u89C1\u7684\u8D44\u6E90\u7BA1\u7406\u5668\uFF08 RM \uFF09\u662F\u6570\u636E\u5E93\uFF0C\u5E38\u89C1\u7684\u901A\u4FE1\u8D44\u6E90\u7BA1\u7406\u5668\uFF08 CRM \uFF09\u662F\u6D88\u606F\u4E2D\u95F4\u4EF6\u3002 \u901A\u5E38\u628A\u4E00\u4E2A\u6570\u636E\u5E93\u5185\u90E8\u7684\u4E8B\u52A1\u5904\u7406\uFF0C\u5982\u5BF9\u591A\u4E2A\u8868\u7684\u64CD\u4F5C\uFF0C\u4F5C\u4E3A\u672C\u5730\u4E8B\u52A1\u770B\u5F85\u3002\u6570\u636E\u5E93\u7684\u4E8B\u52A1\u5904\u7406\u5BF9\u8C61\u662F\u672C\u5730\u4E8B\u52A1\uFF0C\u800C\u5206\u5E03\u5F0F\u4E8B\u52A1\u5904\u7406\u7684\u5BF9\u8C61\u662F\u5168\u5C40\u4E8B\u52A1\u3002 \u6240\u8C13\u5168\u5C40\u4E8B\u52A1\uFF0C\u662F\u6307\u5206\u5E03\u5F0F\u4E8B\u52A1\u5904\u7406\u73AF\u5883\u4E2D\uFF0C\u591A\u4E2A\u6570\u636E\u5E93\u53EF\u80FD\u9700\u8981\u5171\u540C\u5B8C\u6210\u4E00\u4E2A\u5DE5\u4F5C\uFF0C\u8FD9\u4E2A\u5DE5\u4F5C\u5373\u662F\u4E00\u4E2A\u5168\u5C40\u4E8B\u52A1\uFF0C\u4F8B\u5982\uFF0C\u4E00\u4E2A\u4E8B\u52A1\u4E2D\u53EF\u80FD\u66F4\u65B0\u51E0\u4E2A\u4E0D\u540C\u7684\u6570\u636E\u5E93\u3002\u5BF9\u6570\u636E\u5E93\u7684\u64CD\u4F5C\u53D1\u751F\u5728\u7CFB\u7EDF\u7684\u5404\u5904\u4F46\u5FC5\u987B\u5168\u90E8\u88AB\u63D0\u4EA4\u6216\u56DE\u6EDA\u3002\u6B64\u65F6\u4E00\u4E2A\u6570\u636E\u5E93\u5BF9\u81EA\u5DF1\u5185\u90E8\u6240\u505A\u64CD\u4F5C\u7684\u63D0\u4EA4\u4E0D\u4EC5\u4F9D\u8D56\u672C\u8EAB\u64CD\u4F5C\u662F\u5426\u6210\u529F\uFF0C\u8FD8\u8981\u4F9D\u8D56\u4E0E\u5168\u5C40\u4E8B\u52A1\u76F8\u5173\u7684\u5176\u5B83\u6570\u636E\u5E93\u7684\u64CD\u4F5C\u662F\u5426\u6210\u529F\uFF0C\u5982\u679C\u4EFB\u4E00\u6570\u636E\u5E93\u7684\u4EFB\u4E00\u64CD\u4F5C\u5931\u8D25\uFF0C\u5219\u53C2\u4E0E\u6B64\u4E8B\u52A1\u7684\u6240\u6709\u6570\u636E\u5E93\u6240\u505A\u7684\u6240\u6709\u64CD\u4F5C\u90FD\u5FC5\u987B\u56DE\u6EDA\u3002 \u4E00\u822C\u60C5\u51B5\u4E0B\uFF0C\u67D0\u4E00\u6570\u636E\u5E93\u65E0\u6CD5\u77E5\u9053\u5176\u5B83\u6570\u636E\u5E93\u5728\u505A\u4EC0\u4E48\uFF0C\u56E0\u6B64\uFF0C\u5728\u4E00\u4E2A DTP \u73AF\u5883\u4E2D\uFF0C\u4EA4\u6613\u4E2D\u95F4\u4EF6\u662F\u5FC5\u9700\u7684\uFF0C\u7531\u5B83\u901A\u77E5\u548C\u534F\u8C03\u76F8\u5173\u6570\u636E\u5E93\u7684\u63D0\u4EA4\u6216\u56DE\u6EDA\u3002\u800C\u4E00\u4E2A\u6570\u636E\u5E93\u53EA\u5C06\u5176\u81EA\u5DF1\u6240\u505A\u7684\u64CD\u4F5C\uFF08\u53EF\u6062\u590D\uFF09\u5F71\u5C04\u5230\u5168\u5C40\u4E8B\u52A1\u4E2D\u3002</p><blockquote><p>XA \u5C31\u662F X/Open DTP \u5B9A\u4E49\u7684\u4EA4\u6613\u4E2D\u95F4\u4EF6\u4E0E\u6570\u636E\u5E93\u4E4B\u95F4\u7684\u63A5\u53E3\u89C4\u8303\uFF08\u5373\u63A5\u53E3\u51FD\u6570\uFF09\uFF0C\u4EA4\u6613\u4E2D\u95F4\u4EF6\u7528\u5B83\u6765\u901A\u77E5\u6570\u636E\u5E93\u4E8B\u52A1\u7684\u5F00\u59CB\u3001\u7ED3\u675F\u4EE5\u53CA\u63D0\u4EA4\u3001\u56DE\u6EDA\u7B49\u3002 XA \u63A5\u53E3\u51FD\u6570\u7531\u6570\u636E\u5E93\u5382\u5546\u63D0\u4F9B\u3002</p></blockquote><p>\u4E8C\u9636\u63D0\u4EA4\u534F\u8BAE\u548C\u4E09\u9636\u63D0\u4EA4\u534F\u8BAE\u5C31\u662F\u6839\u636E\u8FD9\u4E00\u601D\u60F3\u884D\u751F\u51FA\u6765\u7684\u3002\u53EF\u4EE5\u8BF4\u4E8C\u9636\u6BB5\u63D0\u4EA4\u5176\u5B9E\u5C31\u662F\u5B9E\u73B0 XA \u5206\u5E03\u5F0F\u4E8B\u52A1\u7684\u5173\u952E(\u786E\u5207\u5730\u8BF4\uFF1A\u4E24\u9636\u6BB5\u63D0\u4EA4\u4E3B\u8981\u4FDD\u8BC1\u4E86\u5206\u5E03\u5F0F\u4E8B\u52A1\u7684\u539F\u5B50\u6027\uFF1A\u5373\u6240\u6709\u7ED3\u70B9\u8981\u4E48\u5168\u505A\u8981\u4E48\u5168\u4E0D\u505A)</p><h2 id="_2pc" tabindex="-1"><a class="header-anchor" href="#_2pc" aria-hidden="true">#</a> 2PC</h2><blockquote><p>\u4E8C\u9636\u6BB5\u63D0\u4EA4(Two-phaseCommit)\u662F\u6307\uFF0C\u5728\u8BA1\u7B97\u673A\u7F51\u7EDC\u4EE5\u53CA\u6570\u636E\u5E93\u9886\u57DF\u5185\uFF0C\u4E3A\u4E86\u4F7F\u57FA\u4E8E\u5206\u5E03\u5F0F\u7CFB\u7EDF\u67B6\u6784\u4E0B\u7684\u6240\u6709\u8282\u70B9\u5728\u8FDB\u884C\u4E8B\u52A1\u63D0\u4EA4\u65F6\u4FDD\u6301\u4E00\u81F4\u6027\u800C\u8BBE\u8BA1\u7684\u4E00\u79CD\u7B97\u6CD5(Algorithm)\u3002\u901A\u5E38\uFF0C\u4E8C\u9636\u6BB5\u63D0\u4EA4\u4E5F\u88AB\u79F0\u4E3A\u662F\u4E00\u79CD\u534F\u8BAE(Protocol))\u3002\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u6BCF\u4E2A\u8282\u70B9\u867D\u7136\u53EF\u4EE5\u77E5\u6653\u81EA\u5DF1\u7684\u64CD\u4F5C\u65F6\u6210\u529F\u6216\u8005\u5931\u8D25\uFF0C\u5374\u65E0\u6CD5\u77E5\u9053\u5176\u4ED6\u8282\u70B9\u7684\u64CD\u4F5C\u7684\u6210\u529F\u6216\u5931\u8D25\u3002\u5F53\u4E00\u4E2A\u4E8B\u52A1\u8DE8\u8D8A\u591A\u4E2A\u8282\u70B9\u65F6\uFF0C\u4E3A\u4E86\u4FDD\u6301\u4E8B\u52A1\u7684ACID\u7279\u6027\uFF0C\u9700\u8981\u5F15\u5165\u4E00\u4E2A\u4F5C\u4E3A\u534F\u8C03\u8005\u7684\u7EC4\u4EF6\u6765\u7EDF\u4E00\u638C\u63A7\u6240\u6709\u8282\u70B9(\u79F0\u4F5C\u53C2\u4E0E\u8005)\u7684\u64CD\u4F5C\u7ED3\u679C\u5E76\u6700\u7EC8\u6307\u793A\u8FD9\u4E9B\u8282\u70B9\u662F\u5426\u8981\u628A\u64CD\u4F5C\u7ED3\u679C\u8FDB\u884C\u771F\u6B63\u7684\u63D0\u4EA4(\u6BD4\u5982\u5C06\u66F4\u65B0\u540E\u7684\u6570\u636E\u5199\u5165\u78C1\u76D8\u7B49\u7B49)\u3002\u56E0\u6B64\uFF0C<strong>\u4E8C\u9636\u6BB5\u63D0\u4EA4\u7684\u7B97\u6CD5\u601D\u8DEF\u53EF\u4EE5\u6982\u62EC\u4E3A\uFF1A\u53C2\u4E0E\u8005\u5C06\u64CD\u4F5C\u6210\u8D25\u901A\u77E5\u534F\u8C03\u8005\uFF0C\u518D\u7531\u534F\u8C03\u8005\u6839\u636E\u6240\u6709\u53C2\u4E0E\u8005\u7684\u53CD\u9988\u60C5\u62A5\u51B3\u5B9A\u5404\u53C2\u4E0E\u8005\u662F\u5426\u8981\u63D0\u4EA4\u64CD\u4F5C\u8FD8\u662F\u4E2D\u6B62\u64CD\u4F5C</strong>\u3002</p></blockquote><p>\u6240\u8C13\u7684\u4E24\u4E2A\u9636\u6BB5\u662F\u6307\uFF1A\u7B2C\u4E00\u9636\u6BB5\uFF1A<strong>\u51C6\u5907\u9636\u6BB5(\u6295\u7968\u9636\u6BB5)</strong> \u548C\u7B2C\u4E8C\u9636\u6BB5\uFF1A<strong>\u63D0\u4EA4\u9636\u6BB5\uFF08\u6267\u884C\u9636\u6BB5\uFF09</strong>\u3002</p><h3 id="\u51C6\u5907\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u51C6\u5907\u9636\u6BB5" aria-hidden="true">#</a> \u51C6\u5907\u9636\u6BB5</h3><p>\u4E8B\u52A1\u534F\u8C03\u8005(\u4E8B\u52A1\u7BA1\u7406\u5668)\u7ED9\u6BCF\u4E2A\u53C2\u4E0E\u8005(\u8D44\u6E90\u7BA1\u7406\u5668)\u53D1\u9001 Prepare \u6D88\u606F\uFF0C\u6BCF\u4E2A\u53C2\u4E0E\u8005\u8981\u4E48\u76F4\u63A5\u8FD4\u56DE\u5931\u8D25(\u5982\u6743\u9650\u9A8C\u8BC1\u5931\u8D25)\uFF0C\u8981\u4E48\u5728\u672C\u5730\u6267\u884C\u4E8B\u52A1\uFF0C\u5199\u672C\u5730\u7684 redo \u548C undo \u65E5\u5FD7\uFF0C\u4F46\u4E0D\u63D0\u4EA4\uFF0C\u5230\u8FBE\u4E00\u79CD\u201C\u4E07\u4E8B\u4FF1\u5907\uFF0C\u53EA\u6B20\u4E1C\u98CE\u201D\u7684\u72B6\u6001\u3002</p><p>\u53EF\u4EE5\u8FDB\u4E00\u6B65\u5C06\u51C6\u5907\u9636\u6BB5\u5206\u4E3A\u4EE5\u4E0B\u4E09\u4E2A\u6B65\u9AA4\uFF1A</p><ol><li>\u534F\u8C03\u8005\u8282\u70B9\u5411\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u8BE2\u95EE\u662F\u5426\u53EF\u4EE5\u6267\u884C\u63D0\u4EA4\u64CD\u4F5C(vote)\uFF0C\u5E76\u5F00\u59CB\u7B49\u5F85\u5404\u53C2\u4E0E\u8005\u8282\u70B9\u7684\u54CD\u5E94\u3002</li><li>\u53C2\u4E0E\u8005\u8282\u70B9\u6267\u884C\u8BE2\u95EE\u53D1\u8D77\u4E3A\u6B62\u7684\u6240\u6709\u4E8B\u52A1\u64CD\u4F5C\uFF0C\u5E76\u5C06 Undo \u4FE1\u606F\u548C Redo \u4FE1\u606F\u5199\u5165\u65E5\u5FD7\u3002\uFF08\u6CE8\u610F\uFF1A\u82E5\u6210\u529F\u8FD9\u91CC\u5176\u5B9E\u6BCF\u4E2A\u53C2\u4E0E\u8005\u5DF2\u7ECF\u6267\u884C\u4E86\u4E8B\u52A1\u64CD\u4F5C\uFF09</li><li>\u5404\u53C2\u4E0E\u8005\u8282\u70B9\u54CD\u5E94\u534F\u8C03\u8005\u8282\u70B9\u53D1\u8D77\u7684\u8BE2\u95EE\u3002\u5982\u679C\u53C2\u4E0E\u8005\u8282\u70B9\u7684\u4E8B\u52A1\u64CD\u4F5C\u5B9E\u9645\u6267\u884C\u6210\u529F\uFF0C\u5219\u5B83\u8FD4\u56DE\u4E00\u4E2A\u201D\u540C\u610F\u201D\u6D88\u606F\uFF1B\u5982\u679C\u53C2\u4E0E\u8005\u8282\u70B9\u7684\u4E8B\u52A1\u64CD\u4F5C\u5B9E\u9645\u6267\u884C\u5931\u8D25\uFF0C\u5219\u5B83\u8FD4\u56DE\u4E00\u4E2A\u201D\u4E2D\u6B62\u201D\u6D88\u606F\u3002</li></ol><h3 id="\u63D0\u4EA4\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u63D0\u4EA4\u9636\u6BB5" aria-hidden="true">#</a> \u63D0\u4EA4\u9636\u6BB5</h3><p>\u5982\u679C\u534F\u8C03\u8005\u6536\u5230\u4E86\u53C2\u4E0E\u8005\u7684\u5931\u8D25\u6D88\u606F\u6216\u8005\u8D85\u65F6\uFF0C\u76F4\u63A5\u7ED9\u6BCF\u4E2A\u53C2\u4E0E\u8005\u53D1\u9001\u56DE\u6EDA( Rollback )\u6D88\u606F\uFF1B\u5426\u5219\uFF0C\u53D1\u9001\u63D0\u4EA4( Commit )\u6D88\u606F\uFF1B\u53C2\u4E0E\u8005\u6839\u636E\u534F\u8C03\u8005\u7684\u6307\u4EE4\u6267\u884C\u63D0\u4EA4\u6216\u8005\u56DE\u6EDA\u64CD\u4F5C\uFF0C\u91CA\u653E\u6240\u6709\u4E8B\u52A1\u5904\u7406\u8FC7\u7A0B\u4E2D\u4F7F\u7528\u7684\u9501\u8D44\u6E90\u3002(\u6CE8\u610F:\u5FC5\u987B\u5728\u6700\u540E\u9636\u6BB5\u91CA\u653E\u9501\u8D44\u6E90)</p><p>\u63A5\u4E0B\u6765\u5206\u4E24\u79CD\u60C5\u51B5\u5206\u522B\u8BA8\u8BBA\u63D0\u4EA4\u9636\u6BB5\u7684\u8FC7\u7A0B\u3002</p><p>\u5F53\u534F\u8C03\u8005\u8282\u70B9\u4ECE\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u83B7\u5F97\u7684\u76F8\u5E94\u6D88\u606F\u90FD\u4E3A\u201D\u540C\u610F\u201D\u65F6\uFF1A</p><p><img src="https://www.funtl.com/assets/success.png" alt="img"></p><ol><li>\u534F\u8C03\u8005\u8282\u70B9\u5411\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u53D1\u51FA\u201D\u6B63\u5F0F\u63D0\u4EA4( commit )\u201D\u7684\u8BF7\u6C42\u3002</li><li>\u53C2\u4E0E\u8005\u8282\u70B9\u6B63\u5F0F\u5B8C\u6210\u64CD\u4F5C\uFF0C\u5E76\u91CA\u653E\u5728\u6574\u4E2A\u4E8B\u52A1\u671F\u95F4\u5185\u5360\u7528\u7684\u8D44\u6E90\u3002</li><li>\u53C2\u4E0E\u8005\u8282\u70B9\u5411\u534F\u8C03\u8005\u8282\u70B9\u53D1\u9001\u201D\u5B8C\u6210\u201D\u6D88\u606F\u3002</li><li>\u534F\u8C03\u8005\u8282\u70B9\u53D7\u5230\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u53CD\u9988\u7684\u201D\u5B8C\u6210\u201D\u6D88\u606F\u540E\uFF0C\u5B8C\u6210\u4E8B\u52A1\u3002</li></ol><p>\u5982\u679C\u4EFB\u4E00\u53C2\u4E0E\u8005\u8282\u70B9\u5728\u7B2C\u4E00\u9636\u6BB5\u8FD4\u56DE\u7684\u54CD\u5E94\u6D88\u606F\u4E3A\u201D\u4E2D\u6B62\u201D\uFF0C\u6216\u8005 \u534F\u8C03\u8005\u8282\u70B9\u5728\u7B2C\u4E00\u9636\u6BB5\u7684\u8BE2\u95EE\u8D85\u65F6\u4E4B\u524D\u65E0\u6CD5\u83B7\u53D6\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u7684\u54CD\u5E94\u6D88\u606F\u65F6\uFF1A</p><p><img src="https://www.funtl.com/assets/fail.png" alt="img"></p><ol><li>\u534F\u8C03\u8005\u8282\u70B9\u5411\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u53D1\u51FA\u201D\u56DE\u6EDA\u64CD\u4F5C( rollback )\u201D\u7684\u8BF7\u6C42\u3002</li><li>\u53C2\u4E0E\u8005\u8282\u70B9\u5229\u7528\u4E4B\u524D\u5199\u5165\u7684 Undo \u4FE1\u606F\u6267\u884C\u56DE\u6EDA\uFF0C\u5E76\u91CA\u653E\u5728\u6574\u4E2A\u4E8B\u52A1\u671F\u95F4\u5185\u5360\u7528\u7684\u8D44\u6E90\u3002</li><li>\u53C2\u4E0E\u8005\u8282\u70B9\u5411\u534F\u8C03\u8005\u8282\u70B9\u53D1\u9001\u201D\u56DE\u6EDA\u5B8C\u6210\u201D\u6D88\u606F\u3002</li><li>\u534F\u8C03\u8005\u8282\u70B9\u53D7\u5230\u6240\u6709\u53C2\u4E0E\u8005\u8282\u70B9\u53CD\u9988\u7684\u201D\u56DE\u6EDA\u5B8C\u6210\u201D\u6D88\u606F\u540E\uFF0C\u53D6\u6D88\u4E8B\u52A1\u3002</li></ol><p><strong>\u4E0D\u7BA1\u6700\u540E\u7ED3\u679C\u5982\u4F55\uFF0C\u7B2C\u4E8C\u9636\u6BB5\u90FD\u4F1A\u7ED3\u675F\u5F53\u524D\u4E8B\u52A1\u3002</strong></p><p>\u4E8C\u9636\u6BB5\u63D0\u4EA4\u770B\u8D77\u6765\u786E\u5B9E\u80FD\u591F\u63D0\u4F9B\u539F\u5B50\u6027\u7684\u64CD\u4F5C\uFF0C\u4F46\u662F\u4E0D\u5E78\u7684\u4E8B\uFF0C\u4E8C\u9636\u6BB5\u63D0\u4EA4\u8FD8\u662F\u6709\u51E0\u4E2A<strong>\u7F3A\u70B9</strong>\u7684\uFF1A</p><ol><li><strong>\u540C\u6B65\u963B\u585E\u95EE\u9898</strong>\uFF1A\u6267\u884C\u8FC7\u7A0B\u4E2D\uFF0C\u6240\u6709\u53C2\u4E0E\u8282\u70B9\u90FD\u662F\u4E8B\u52A1\u963B\u585E\u578B\u7684\u3002\u5F53\u53C2\u4E0E\u8005\u5360\u6709\u516C\u5171\u8D44\u6E90\u65F6\uFF0C\u5176\u4ED6\u7B2C\u4E09\u65B9\u8282\u70B9\u8BBF\u95EE\u516C\u5171\u8D44\u6E90\u4E0D\u5F97\u4E0D\u5904\u4E8E\u963B\u585E\u72B6\u6001\u3002</li><li><strong>\u5355\u70B9\u6545\u969C</strong>\uFF1A\u7531\u4E8E\u534F\u8C03\u8005\u7684\u91CD\u8981\u6027\uFF0C\u4E00\u65E6\u534F\u8C03\u8005\u53D1\u751F\u6545\u969C\u3002\u53C2\u4E0E\u8005\u4F1A\u4E00\u76F4\u963B\u585E\u4E0B\u53BB\u3002\u5C24\u5176\u5728\u7B2C\u4E8C\u9636\u6BB5\uFF0C\u534F\u8C03\u8005\u53D1\u751F\u6545\u969C\uFF0C\u90A3\u4E48\u6240\u6709\u7684\u53C2\u4E0E\u8005\u8FD8\u90FD\u5904\u4E8E\u9501\u5B9A\u4E8B\u52A1\u8D44\u6E90\u7684\u72B6\u6001\u4E2D\uFF0C\u800C\u65E0\u6CD5\u7EE7\u7EED\u5B8C\u6210\u4E8B\u52A1\u64CD\u4F5C\u3002\uFF08\u5982\u679C\u662F\u534F\u8C03\u8005\u6302\u6389\uFF0C\u53EF\u4EE5\u91CD\u65B0\u9009\u4E3E\u4E00\u4E2A\u534F\u8C03\u8005\uFF0C\u4F46\u662F\u65E0\u6CD5\u89E3\u51B3\u56E0\u4E3A\u534F\u8C03\u8005\u5B95\u673A\u5BFC\u81F4\u7684\u53C2\u4E0E\u8005\u5904\u4E8E\u963B\u585E\u72B6\u6001\u7684\u95EE\u9898\uFF09</li><li><strong>\u6570\u636E\u4E0D\u4E00\u81F4</strong>\uFF1A\u5728\u4E8C\u9636\u6BB5\u63D0\u4EA4\u7684\u9636\u6BB5\u4E8C\u4E2D\uFF0C\u5F53\u534F\u8C03\u8005\u5411\u53C2\u4E0E\u8005\u53D1\u9001 commit \u8BF7\u6C42\u4E4B\u540E\uFF0C\u53D1\u751F\u4E86\u5C40\u90E8\u7F51\u7EDC\u5F02\u5E38\u6216\u8005\u5728\u53D1\u9001 commit \u8BF7\u6C42\u8FC7\u7A0B\u4E2D\u534F\u8C03\u8005\u53D1\u751F\u4E86\u6545\u969C\uFF0C\u8FD9\u56DE\u5BFC\u81F4\u53EA\u6709\u4E00\u90E8\u5206\u53C2\u4E0E\u8005\u63A5\u53D7\u5230\u4E86 commit \u8BF7\u6C42\u3002\u800C\u5728\u8FD9\u90E8\u5206\u53C2\u4E0E\u8005\u63A5\u5230 commit \u8BF7\u6C42\u4E4B\u540E\u5C31\u4F1A\u6267\u884C commit \u64CD\u4F5C\u3002\u4F46\u662F\u5176\u4ED6\u90E8\u5206\u672A\u63A5\u5230 commit \u8BF7\u6C42\u7684\u673A\u5668\u5219\u65E0\u6CD5\u6267\u884C\u4E8B\u52A1\u63D0\u4EA4\u3002\u4E8E\u662F\u6574\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4FBF\u51FA\u73B0\u4E86\u6570\u636E\u90E8\u4E00\u81F4\u6027\u7684\u73B0\u8C61\u3002</li><li>\u4E8C\u9636\u6BB5\u65E0\u6CD5\u89E3\u51B3\u7684\u95EE\u9898\uFF1A\u534F\u8C03\u8005\u518D\u53D1\u51FA commit \u6D88\u606F\u4E4B\u540E\u5B95\u673A\uFF0C\u800C\u552F\u4E00\u63A5\u6536\u5230\u8FD9\u6761\u6D88\u606F\u7684\u53C2\u4E0E\u8005\u540C\u65F6\u4E5F\u5B95\u673A\u4E86\u3002\u90A3\u4E48\u5373\u4F7F\u534F\u8C03\u8005\u901A\u8FC7\u9009\u4E3E\u534F\u8BAE\u4EA7\u751F\u4E86\u65B0\u7684\u534F\u8C03\u8005\uFF0C\u8FD9\u6761\u4E8B\u52A1\u7684\u72B6\u6001\u4E5F\u662F\u4E0D\u786E\u5B9A\u7684\uFF0C\u6CA1\u4EBA\u77E5\u9053\u4E8B\u52A1\u662F\u5426\u88AB\u5DF2\u7ECF\u63D0\u4EA4\u3002</li></ol><p>\u7531\u4E8E\u4E8C\u9636\u6BB5\u63D0\u4EA4\u5B58\u5728\u7740\u8BF8\u5982\u540C\u6B65\u963B\u585E\u3001\u5355\u70B9\u95EE\u9898\u3001\u8111\u88C2\u7B49\u7F3A\u9677\uFF0C\u6240\u4EE5\uFF0C\u7814\u7A76\u8005\u4EEC\u5728\u4E8C\u9636\u6BB5\u63D0\u4EA4\u7684\u57FA\u7840\u4E0A\u505A\u4E86\u6539\u8FDB\uFF0C\u63D0\u51FA\u4E86\u4E09\u9636\u6BB5\u63D0\u4EA4\u3002</p><h2 id="_3pc" tabindex="-1"><a class="header-anchor" href="#_3pc" aria-hidden="true">#</a> 3PC</h2><blockquote><p>\u4E09\u9636\u6BB5\u63D0\u4EA4\uFF08Three-phase commit\uFF09\uFF0C\u4E5F\u53EB\u4E09\u9636\u6BB5\u63D0\u4EA4\u534F\u8BAE\uFF08Three-phase commit protocol\uFF09\uFF0C\u662F\u4E8C\u9636\u6BB5\u63D0\u4EA4\uFF082PC\uFF09\u7684\u6539\u8FDB\u7248\u672C\u3002</p></blockquote><p><img src="https://www.funtl.com/assets/3pccommit.png" alt="img"></p><p>\u4E0E\u4E24\u9636\u6BB5\u63D0\u4EA4\u4E0D\u540C\u7684\u662F\uFF0C\u4E09\u9636\u6BB5\u63D0\u4EA4\u6709\u4E24\u4E2A\u6539\u52A8\u70B9\u3002</p><ol><li>\u5F15\u5165\u8D85\u65F6\u673A\u5236\u3002\u540C\u65F6\u5728\u534F\u8C03\u8005\u548C\u53C2\u4E0E\u8005\u4E2D\u90FD\u5F15\u5165\u8D85\u65F6\u673A\u5236\u3002</li><li>\u5728\u7B2C\u4E00\u9636\u6BB5\u548C\u7B2C\u4E8C\u9636\u6BB5\u4E2D\u63D2\u5165\u4E00\u4E2A\u51C6\u5907\u9636\u6BB5\u3002\u4FDD\u8BC1\u4E86\u5728\u6700\u540E\u63D0\u4EA4\u9636\u6BB5\u4E4B\u524D\u5404\u53C2\u4E0E\u8282\u70B9\u7684\u72B6\u6001\u662F\u4E00\u81F4\u7684\u3002</li></ol><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u9664\u4E86\u5F15\u5165\u8D85\u65F6\u673A\u5236\u4E4B\u5916\uFF0C3PC \u628A 2PC \u7684\u51C6\u5907\u9636\u6BB5\u518D\u6B21\u4E00\u5206\u4E3A\u4E8C\uFF0C\u8FD9\u6837\u4E09\u9636\u6BB5\u63D0\u4EA4\u5C31\u6709 <code>CanCommit</code>\u3001<code>PreCommit</code>\u3001<code>DoCommit</code> \u4E09\u4E2A\u9636\u6BB5\u3002</p><h3 id="cancommit-\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#cancommit-\u9636\u6BB5" aria-hidden="true">#</a> CanCommit \u9636\u6BB5</h3><p>3PC \u7684 CanCommit \u9636\u6BB5\u5176\u5B9E\u548C 2PC \u7684\u51C6\u5907\u9636\u6BB5\u5F88\u50CF\u3002\u534F\u8C03\u8005\u5411\u53C2\u4E0E\u8005\u53D1\u9001 commit \u8BF7\u6C42\uFF0C\u53C2\u4E0E\u8005\u5982\u679C\u53EF\u4EE5\u63D0\u4EA4\u5C31\u8FD4\u56DE Yes \u54CD\u5E94\uFF0C\u5426\u5219\u8FD4\u56DE No \u54CD\u5E94\u3002</p><ol><li><strong>\u4E8B\u52A1\u8BE2\u95EE</strong>\uFF1A\u534F\u8C03\u8005\u5411\u53C2\u4E0E\u8005\u53D1\u9001CanCommit\u8BF7\u6C42\u3002\u8BE2\u95EE\u662F\u5426\u53EF\u4EE5\u6267\u884C\u4E8B\u52A1\u63D0\u4EA4\u64CD\u4F5C\u3002\u7136\u540E\u5F00\u59CB\u7B49\u5F85\u53C2\u4E0E\u8005\u7684\u54CD\u5E94\u3002</li><li><strong>\u54CD\u5E94\u53CD\u9988</strong>\uFF1A\u53C2\u4E0E\u8005\u63A5\u5230CanCommit\u8BF7\u6C42\u4E4B\u540E\uFF0C\u6B63\u5E38\u60C5\u51B5\u4E0B\uFF0C\u5982\u679C\u5176\u81EA\u8EAB\u8BA4\u4E3A\u53EF\u4EE5\u987A\u5229\u6267\u884C\u4E8B\u52A1\uFF0C\u5219\u8FD4\u56DEYes\u54CD\u5E94\uFF0C\u5E76\u8FDB\u5165\u9884\u5907\u72B6\u6001\u3002\u5426\u5219\u53CD\u9988No</li></ol><h3 id="precommit-\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#precommit-\u9636\u6BB5" aria-hidden="true">#</a> PreCommit \u9636\u6BB5</h3><p>\u534F\u8C03\u8005\u6839\u636E\u53C2\u4E0E\u8005\u7684\u53CD\u5E94\u60C5\u51B5\u6765\u51B3\u5B9A\u662F\u5426\u53EF\u4EE5\u8BB0\u6027\u4E8B\u52A1\u7684 PreCommit \u64CD\u4F5C\u3002\u6839\u636E\u54CD\u5E94\u60C5\u51B5\uFF0C\u6709\u4EE5\u4E0B\u4E24\u79CD\u53EF\u80FD\u3002</p><p><strong>1. \u5047\u5982\u534F\u8C03\u8005\u4ECE\u6240\u6709\u7684\u53C2\u4E0E\u8005\u83B7\u5F97\u7684\u53CD\u9988\u90FD\u662F Yes \u54CD\u5E94\uFF0C\u90A3\u4E48\u5C31\u4F1A\u6267\u884C\u4E8B\u52A1\u7684\u9884\u6267\u884C\u3002</strong></p><ul><li>\u53D1\u9001\u9884\u63D0\u4EA4\u8BF7\u6C42\uFF1A\u534F\u8C03\u8005\u5411\u53C2\u4E0E\u8005\u53D1\u9001 PreCommit \u8BF7\u6C42\uFF0C\u5E76\u8FDB\u5165 Prepared \u9636\u6BB5\u3002</li><li>\u4E8B\u52A1\u9884\u63D0\u4EA4\uFF1A\u53C2\u4E0E\u8005\u63A5\u6536\u5230 PreCommit \u8BF7\u6C42\u540E\uFF0C\u4F1A\u6267\u884C\u4E8B\u52A1\u64CD\u4F5C\uFF0C\u5E76\u5C06 undo \u548C redo \u4FE1\u606F\u8BB0\u5F55\u5230\u4E8B\u52A1\u65E5\u5FD7\u4E2D\u3002</li><li>\u54CD\u5E94\u53CD\u9988\uFF1A\u5982\u679C\u53C2\u4E0E\u8005\u6210\u529F\u7684\u6267\u884C\u4E86\u4E8B\u52A1\u64CD\u4F5C\uFF0C\u5219\u8FD4\u56DE ACK \u54CD\u5E94\uFF0C\u540C\u65F6\u5F00\u59CB\u7B49\u5F85\u6700\u7EC8\u6307\u4EE4\u3002</li></ul><p><strong>2. \u5047\u5982\u6709\u4EFB\u4F55\u4E00\u4E2A\u53C2\u4E0E\u8005\u5411\u534F\u8C03\u8005\u53D1\u9001\u4E86 No \u54CD\u5E94\uFF0C\u6216\u8005\u7B49\u5F85\u8D85\u65F6\u4E4B\u540E\uFF0C\u534F\u8C03\u8005\u90FD\u6CA1\u6709\u63A5\u5230\u53C2\u4E0E\u8005\u7684\u54CD\u5E94\uFF0C\u90A3\u4E48\u5C31\u6267\u884C\u4E8B\u52A1\u7684\u4E2D\u65AD\u3002</strong></p><ul><li>\u53D1\u9001\u4E2D\u65AD\u8BF7\u6C42\uFF1A\u534F\u8C03\u8005\u5411\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 abort \u8BF7\u6C42\u3002</li><li>\u4E2D\u65AD\u4E8B\u52A1\uFF1A\u53C2\u4E0E\u8005\u6536\u5230\u6765\u81EA\u534F\u8C03\u8005\u7684 abort \u8BF7\u6C42\u4E4B\u540E\uFF08\u6216\u8D85\u65F6\u4E4B\u540E\uFF0C\u4ECD\u672A\u6536\u5230\u534F\u8C03\u8005\u7684\u8BF7\u6C42\uFF09\uFF0C\u6267\u884C\u4E8B\u52A1\u7684\u4E2D\u65AD\u3002</li></ul><h3 id="docommit-\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#docommit-\u9636\u6BB5" aria-hidden="true">#</a> doCommit \u9636\u6BB5</h3><p>\u8BE5\u9636\u6BB5\u8FDB\u884C\u771F\u6B63\u7684\u4E8B\u52A1\u63D0\u4EA4\uFF0C\u4E5F\u53EF\u4EE5\u5206\u4E3A\u4EE5\u4E0B\u4E24\u79CD\u60C5\u51B5\u3002</p><p><strong>1. \u6267\u884C\u63D0\u4EA4</strong></p><ul><li>\u53D1\u9001\u63D0\u4EA4\u8BF7\u6C42\uFF1A\u534F\u8C03\u63A5\u6536\u5230\u53C2\u4E0E\u8005\u53D1\u9001\u7684 ACK \u54CD\u5E94\uFF0C\u90A3\u4E48\u4ED6\u5C06\u4ECE\u9884\u63D0\u4EA4\u72B6\u6001\u8FDB\u5165\u5230\u63D0\u4EA4\u72B6\u6001\u3002\u5E76\u5411\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 doCommit \u8BF7\u6C42\u3002</li><li>\u4E8B\u52A1\u63D0\u4EA4\uFF1A\u53C2\u4E0E\u8005\u63A5\u6536\u5230 doCommit \u8BF7\u6C42\u4E4B\u540E\uFF0C\u6267\u884C\u6B63\u5F0F\u7684\u4E8B\u52A1\u63D0\u4EA4\u3002\u5E76\u5728\u5B8C\u6210\u4E8B\u52A1\u63D0\u4EA4\u4E4B\u540E\u91CA\u653E\u6240\u6709\u4E8B\u52A1\u8D44\u6E90\u3002</li><li>\u54CD\u5E94\u53CD\u9988\uFF1A\u4E8B\u52A1\u63D0\u4EA4\u5B8C\u4E4B\u540E\uFF0C\u5411\u534F\u8C03\u8005\u53D1\u9001 ACK \u54CD\u5E94\u3002</li><li>\u5B8C\u6210\u4E8B\u52A1\uFF1A\u534F\u8C03\u8005\u63A5\u6536\u5230\u6240\u6709\u53C2\u4E0E\u8005\u7684 ACK \u54CD\u5E94\u4E4B\u540E\uFF0C\u5B8C\u6210\u4E8B\u52A1\u3002</li></ul><p><strong>2. \u4E2D\u65AD\u4E8B\u52A1</strong></p><p>\u534F\u8C03\u8005\u6CA1\u6709\u63A5\u6536\u5230\u53C2\u4E0E\u8005\u53D1\u9001\u7684 ACK \u54CD\u5E94\uFF08\u53EF\u80FD\u662F\u63A5\u53D7\u8005\u53D1\u9001\u7684\u4E0D\u662F ACK \u54CD\u5E94\uFF0C\u4E5F\u53EF\u80FD\u54CD\u5E94\u8D85\u65F6\uFF09\uFF0C\u90A3\u4E48\u5C31\u4F1A\u6267\u884C\u4E2D\u65AD\u4E8B\u52A1\u3002</p><ul><li>\u53D1\u9001\u4E2D\u65AD\u8BF7\u6C42\uFF1A\u534F\u8C03\u8005\u5411\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 abort \u8BF7\u6C42</li><li>\u4E8B\u52A1\u56DE\u6EDA\uFF1A\u53C2\u4E0E\u8005\u63A5\u6536\u5230 abort \u8BF7\u6C42\u4E4B\u540E\uFF0C\u5229\u7528\u5176\u5728\u9636\u6BB5\u4E8C\u8BB0\u5F55\u7684 undo \u4FE1\u606F\u6765\u6267\u884C\u4E8B\u52A1\u7684\u56DE\u6EDA\u64CD\u4F5C\uFF0C\u5E76\u5728\u5B8C\u6210\u56DE\u6EDA\u4E4B\u540E\u91CA\u653E\u6240\u6709\u7684\u4E8B\u52A1\u8D44\u6E90\u3002</li><li>\u53CD\u9988\u7ED3\u679C\uFF1A\u53C2\u4E0E\u8005\u5B8C\u6210\u4E8B\u52A1\u56DE\u6EDA\u4E4B\u540E\uFF0C\u5411\u534F\u8C03\u8005\u53D1\u9001 ACK \u6D88\u606F</li><li>\u4E2D\u65AD\u4E8B\u52A1\uFF1A\u534F\u8C03\u8005\u63A5\u6536\u5230\u53C2\u4E0E\u8005\u53CD\u9988\u7684ACK\u6D88\u606F\u4E4B\u540E\uFF0C\u6267\u884C\u4E8B\u52A1\u7684\u4E2D\u65AD\u3002</li></ul><blockquote><p>\u5728 <code>doCommit</code> \u9636\u6BB5\uFF0C\u5982\u679C\u53C2\u4E0E\u8005\u65E0\u6CD5\u53CA\u65F6\u63A5\u6536\u5230\u6765\u81EA\u534F\u8C03\u8005\u7684 <code>doCommit</code> \u6216\u8005 <code>abort</code> \u8BF7\u6C42\u65F6\uFF0C\u4F1A\u5728\u7B49\u5F85\u8D85\u65F6\u4E4B\u540E\uFF0C\u4F1A\u7EE7\u7EED\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4\u3002\uFF08\u5176\u5B9E\u8FD9\u4E2A\u5E94\u8BE5\u662F\u57FA\u4E8E\u6982\u7387\u6765\u51B3\u5B9A\u7684\uFF0C\u5F53\u8FDB\u5165\u7B2C\u4E09\u9636\u6BB5\u65F6\uFF0C\u8BF4\u660E\u53C2\u4E0E\u8005\u5728\u7B2C\u4E8C\u9636\u6BB5\u5DF2\u7ECF\u6536\u5230\u4E86 <code>PreCommit</code> \u8BF7\u6C42\uFF0C\u90A3\u4E48\u534F\u8C03\u8005\u4EA7\u751F <code>PreCommit</code> \u8BF7\u6C42\u7684\u524D\u63D0\u6761\u4EF6\u662F\u4ED6\u5728\u7B2C\u4E8C\u9636\u6BB5\u5F00\u59CB\u4E4B\u524D\uFF0C\u6536\u5230\u6240\u6709\u53C2\u4E0E\u8005\u7684 <code>CanCommit</code> \u54CD\u5E94\u90FD\u662F <code>Yes</code>\u3002\uFF08\u4E00\u65E6\u53C2\u4E0E\u8005\u6536\u5230\u4E86 <code>PreCommit</code>\uFF0C\u610F\u5473\u4ED6\u77E5\u9053\u5927\u5BB6\u5176\u5B9E\u90FD\u540C\u610F\u4FEE\u6539\u4E86\uFF09\u6240\u4EE5\uFF0C\u4E00\u53E5\u8BDD\u6982\u62EC\u5C31\u662F\uFF0C\u5F53\u8FDB\u5165\u7B2C\u4E09\u9636\u6BB5\u65F6\uFF0C\u7531\u4E8E\u7F51\u7EDC\u8D85\u65F6\u7B49\u539F\u56E0\uFF0C\u867D\u7136\u53C2\u4E0E\u8005\u6CA1\u6709\u6536\u5230 <code>commit</code> \u6216\u8005 <code>abort</code> \u54CD\u5E94\uFF0C\u4F46\u662F\u4ED6\u6709\u7406\u7531\u76F8\u4FE1\uFF1A\u6210\u529F\u63D0\u4EA4\u7684\u51E0\u7387\u5F88\u5927\u3002\uFF09</p></blockquote><h2 id="_2pc-\u4E0E-3pc-\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#_2pc-\u4E0E-3pc-\u7684\u533A\u522B" aria-hidden="true">#</a> 2PC \u4E0E 3PC \u7684\u533A\u522B</h2><p>\u76F8\u5BF9\u4E8E 2PC\uFF0C3PC \u4E3B\u8981\u89E3\u51B3\u7684\u5355\u70B9\u6545\u969C\u95EE\u9898\uFF0C\u5E76\u51CF\u5C11\u963B\u585E\uFF0C\u56E0\u4E3A\u4E00\u65E6\u53C2\u4E0E\u8005\u65E0\u6CD5\u53CA\u65F6\u6536\u5230\u6765\u81EA\u534F\u8C03\u8005\u7684\u4FE1\u606F\u4E4B\u540E\uFF0C\u4ED6\u4F1A\u9ED8\u8BA4\u6267\u884C commit\u3002\u800C\u4E0D\u4F1A\u4E00\u76F4\u6301\u6709\u4E8B\u52A1\u8D44\u6E90\u5E76\u5904\u4E8E\u963B\u585E\u72B6\u6001\u3002\u4F46\u662F\u8FD9\u79CD\u673A\u5236\u4E5F\u4F1A\u5BFC\u81F4\u6570\u636E\u4E00\u81F4\u6027\u95EE\u9898\uFF0C\u56E0\u4E3A\uFF0C\u7531\u4E8E\u7F51\u7EDC\u539F\u56E0\uFF0C\u534F\u8C03\u8005\u53D1\u9001\u7684 abort \u54CD\u5E94\u6CA1\u6709\u53CA\u65F6\u88AB\u53C2\u4E0E\u8005\u63A5\u6536\u5230\uFF0C\u90A3\u4E48\u53C2\u4E0E\u8005\u5728\u7B49\u5F85\u8D85\u65F6\u4E4B\u540E\u6267\u884C\u4E86 commit \u64CD\u4F5C\u3002\u8FD9\u6837\u5C31\u548C\u5176\u4ED6\u63A5\u5230 abort \u547D\u4EE4\u5E76\u6267\u884C\u56DE\u6EDA\u7684\u53C2\u4E0E\u8005\u4E4B\u95F4\u5B58\u5728\u6570\u636E\u4E0D\u4E00\u81F4\u7684\u60C5\u51B5\u3002</p>',56),l=[r];function c(m,d){return i(),e("div",null,l)}const p=o(a,[["render",c],["__file","\u5206\u5E03\u5F0F\u4E8B\u52A1.html.vue"]]);export{p as default};
