import{_ as e,o as i,c as n,a}from"./app.f0c02a19.js";const d={},l=a('<h1 id="\u57FA\u4E8E\u89D2\u8272\u7684\u8BBF\u95EE\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u57FA\u4E8E\u89D2\u8272\u7684\u8BBF\u95EE\u63A7\u5236" aria-hidden="true">#</a> \u57FA\u4E8E\u89D2\u8272\u7684\u8BBF\u95EE\u63A7\u5236</h1><p>\u4EE5\u89D2\u8272\u4E3A\u57FA\u7840\u7684\u8BBF\u95EE\u63A7\u5236\uFF08Role-based access control, RBAC\uFF09\uFF0C\u662F\u8D44\u8BAF\u5B89\u5168\u9886\u57DF\u4E2D\uFF0C\u4E00\u79CD\u8F83\u65B0\u4E14\u5E7F\u4E3A\u4F7F\u7528\u7684\u8BBF\u95EE\u63A7\u5236\u673A\u5236\uFF0C\u5176\u4E0D\u540C\u4E8E\u5F3A\u5236\u8BBF\u95EE\u63A7\u5236\u4EE5\u53CA\u81EA\u7531\u9009\u5B9A\u8BBF\u95EE\u63A7\u5236\u76F4\u63A5\u8D4B\u4E88\u4F7F\u7528\u8005\u6743\u9650\uFF0C\u800C\u662F\u5C06\u6743\u9650\u8D4B\u4E88\u89D2\u8272\u3002\u662F\u4E00\u5957\u8F83\u5F3A\u5236\u8BBF\u95EE\u63A7\u5236\u4EE5\u53CA\u81EA\u7531\u9009\u5B9A\u8BBF\u95EE\u63A7\u5236\u66F4\u4E3A\u4E2D\u6027\u4E14\u66F4\u5177\u7075\u6D3B\u6027\u7684\u8BBF\u95EE\u63A7\u5236\u6280\u672F\u3002</p><h2 id="\u4EC0\u4E48\u662F-rbac" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F-rbac" aria-hidden="true">#</a> \u4EC0\u4E48\u662F RBAC</h2><p>RBAC \u6A21\u578B\u7684\u57FA\u672C\u601D\u60F3\u662F\u5C06\u8BBF\u95EE\u8BB8\u53EF\u6743\u5206\u914D\u7ED9\u4E00\u5B9A\u7684\u89D2\u8272\uFF0C\u7528\u6237\u901A\u8FC7\u9970\u6F14\u4E0D\u540C\u7684\u89D2\u8272\u83B7\u5F97\u89D2\u8272\u6240\u62E5\u6709\u7684\u8BBF\u95EE\u8BB8\u53EF\u6743\u3002</p><p>\u8BBF\u95EE\u63A7\u5236\u53EF\u4EE5\u7B80\u5355\u8868\u8FF0\u4E3A\uFF1A\u5224\u65AD\u8C01\uFF08Who\uFF09\u5BF9\u4EC0\u4E48\uFF08What/Which\uFF09\u8FDB\u884C\u600E\u6837\uFF08How\uFF09\u7684\u64CD\u4F5C\u662F\u5426\u4E3A\u771F\u3002</p><p>\u5728 RBAC \u4E2D\uFF0C\u7528\u6237\u7684\u6743\u9650\u4E0D\u662F\u5728\u7528\u6237\u672C\u8EAB\u4E0A\u8FDB\u884C\u7BA1\u7406\u7684\uFF0C\u7528\u6237\u7684\u6743\u9650\u662F\u7531\u7528\u6237\u6240\u5904\u7684\u89D2\u8272\u6240\u51B3\u5B9A\u7684\u3002\u5728\u6743\u9650\u7BA1\u7406\u4E2D\uFF0C\u901A\u8FC7\u89D2\u8272\u8FD9\u4E00\u6865\u6881\u5C06\u7528\u6237\u4E0E\u6743\u9650\u8054\u7CFB\u8D77\u6765\u3002\u7528\u6237\u548C\u89D2\u8272\u3001\u89D2\u8272\u4E0E\u6743\u9650\u662F\u4E00\u4E2A\u591A\u5BF9\u591A\u7684\u5173\u7CFB\u3002</p><p>\u4E0E RBAC \u8BBF\u95EE\u63A7\u5236\u76F8\u5173\u7684\u6982\u5FF5\u6709\uFF1A</p><ol><li>\u7528\u6237\uFF08User\uFF09\uFF1A\u4E00\u4E2A\u5177\u6709\u552F\u4E00\u6807\u8BC6\u7B26\u7684\u7528\u6237\uFF0C\u4E0E\u6743\u9650\u76F8\u5206\u79BB\uFF0C\u53EA\u80FD\u901A\u8FC7\u6240\u5C5E\u7684 Role \u53BB\u5173\u8054\u6743\u9650\uFF0C\u4E00\u4E2A\u7528\u6237\u53EF\u4EE5\u62E5\u6709\u591A\u9879\u89D2\u8272\uFF1B</li><li>\u89D2\u8272\uFF08Role\uFF09:\u4E00\u5B9A\u6570\u91CF\u7684\u6743\u9650\u7684\u96C6\u5408\uFF0C\u89D2\u8272\u53EF\u4EE5\u7EE7\u627F\uFF0C\u4E00\u4E2A\u89D2\u8272\u5BF9\u5E94\u591A\u9879\u6743\u9650\uFF1B</li><li>\u6743\u9650\uFF08Resource\uFF09\uFF1A\u4E5F\u53EF\u4EE5\u770B\u4F5C\u662F\u8D44\u6E90\uFF0C\u5B83\u5BF9\u5E94\u4E86\u5E94\u7528\u7CFB\u7EDF\u4E2D\u7684\u4E00\u4E2A\u529F\u80FD\uFF1B</li></ol><h2 id="\u8BBE\u8BA1\u5BF9\u5E94\u7684\u6570\u636E\u8868\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u8BA1\u5BF9\u5E94\u7684\u6570\u636E\u8868\u7ED3\u6784" aria-hidden="true">#</a> \u8BBE\u8BA1\u5BF9\u5E94\u7684\u6570\u636E\u8868\u7ED3\u6784</h2><p>\u8FD9\u91CC\u4F7F\u7528\u7684\u662F ThinkPHP \u7684\u8868\u7ED3\u6784\uFF0C\u5B57\u6BB5\u8BBE\u7F6E\u7B26\u5408\u529F\u80FD\u7684\u8BBE\u8BA1\u8981\u6C42\u3002</p><h3 id="\u7528\u6237\u8868" tabindex="-1"><a class="header-anchor" href="#\u7528\u6237\u8868" aria-hidden="true">#</a> \u7528\u6237\u8868</h3><p>\u7B2C\u4E00\u4E2A\u8868\u662F \u7528\u6237\u8868\uFF0C\u90FD\u662F\u4E00\u4E9B\u5F88\u5E38\u7528\u7684\u5B57\u6BB5\uFF0C\u6BD4\u8F83\u597D\u7406\u89E3</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>CREATE TABLE `think_user` (\n	`id` smallint(5) UNSIGNED NOT NULL AUTO_INCREMENT,\n	`account` varchar(64) NOT NULL,\n	`nickname` varchar(50) NOT NULL,\n	`password` char(32) NOT NULL,\n	`email` varchar(50) NOT NULL,\n	`remark` varchar(255) NOT NULL,\n	`create_time` int(11) UNSIGNED NOT NULL,\n	`status` tinyint(1) DEFAULT &#39;0&#39;,\n	PRIMARY KEY (`id`),\n	UNIQUE `account` (`account`)\n) ENGINE = MyISAM AUTO_INCREMENT = 1 CHARSET = utf8\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="\u89D2\u8272\u8868" tabindex="-1"><a class="header-anchor" href="#\u89D2\u8272\u8868" aria-hidden="true">#</a> \u89D2\u8272\u8868</h3><p>\u7B2C\u4E8C\u4E2A\u662F \u89D2\u8272\u8868\uFF0C\u4E5F\u53EF\u4EE5\u7406\u89E3\u4E3A\u5206\u7EC4\u8868</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>CREATE TABLE IF NOT EXISTS `think_role` (\n	`id` smallint(6) UNSIGNED NOT NULL AUTO_INCREMENT,\n	`name` varchar(20) NOT NULL,\n	`pid` smallint(6) DEFAULT NULL,\n	`status` tinyint(1) UNSIGNED DEFAULT NULL,\n	`remark` varchar(255) DEFAULT NULL,\n	PRIMARY KEY (`id`),\n	KEY `pid` (`pid`),\n	KEY `status` (`status`)\n) ENGINE = MyISAM CHARSET = utf8;\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="\u89D2\u8272\u2014\u7528\u6237\u2014\u5173\u7CFB\u6620\u5C04\u8868" tabindex="-1"><a class="header-anchor" href="#\u89D2\u8272\u2014\u7528\u6237\u2014\u5173\u7CFB\u6620\u5C04\u8868" aria-hidden="true">#</a> \u89D2\u8272\u2014\u7528\u6237\u2014\u5173\u7CFB\u6620\u5C04\u8868</h3><p>\u7B2C\u4E09\u4E2A\u662F \u89D2\u8272\u2014\u7528\u6237\u2014\u5173\u7CFB\u6620\u5C04\u8868\uFF0C\u6CE8\u610F\uFF0C\u8FD9\u4E2A\u8868\u7684\u6570\u636E\uFF0C\u53EF\u4EE5\u662F\u591A\u5BF9\u591A\u7684\u5173\u7CFB\uFF0C\u4E5F\u5C31\u662F\u591A\u4E2A\u89D2\u8272\u5BF9\u5E94\u4E00\u4E2A\u7528\u6237\uFF0C\u591A\u4E2A\u7528\u6237\u5BF9\u5E94\u4E00\u4E2A\u89D2\u8272\u3002\u5728\u6211\u7684\u5B9E\u9645\u4F7F\u7528\u4E2D\uFF0C\u6CA1\u6709\u6D89\u53CA\u5230\u4E00\u4E2A\u7528\u6237\u5BF9\u5E94\u591A\u4E2A\u89D2\u8272\u7684\u60C5\u51B5\uFF0C\u4E5F\u662F\u4E3A\u4E86\u7B80\u5316\u64CD\u4F5C\u3002</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>CREATE TABLE IF NOT EXISTS `think_role_user` (\n	`role_id` mediumint(9) UNSIGNED DEFAULT NULL,\n	`user_id` char(32) DEFAULT NULL,\n	KEY `group_id` (`role_id`),\n	KEY `user_id` (`user_id`)\n) ENGINE = MyISAM CHARSET = utf8;\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="\u8D44\u6E90\u8868" tabindex="-1"><a class="header-anchor" href="#\u8D44\u6E90\u8868" aria-hidden="true">#</a> \u8D44\u6E90\u8868</h3><p>\u7B2C\u56DB\u4E2A\u662F\u8282\u70B9\u8868\uFF0C\u4E5F\u5C31\u662F\u8D44\u6E90\u8868\uFF0C\u8981\u6CE8\u610F\u7684\u4E24\u4E2A\u5B57\u6BB5\uFF1Apid \u7528\u6765\u5173\u8054\u8282\u70B9\u7684\u5C42\u7EA7\uFF0Clevel \u7528\u6765\u8BF4\u660E\u8BE5\u8282\u70B9\u662F \u5E94\u7528 \uFF0C\u8FD8\u662F\u6A21\u5757\u548C\u64CD\u4F5C\u3002</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>CREATE TABLE IF NOT EXISTS `think_node` (\n	`id` smallint(6) UNSIGNED NOT NULL AUTO_INCREMENT,\n	`name` varchar(20) NOT NULL,\n	`title` varchar(50) DEFAULT NULL,\n	`status` tinyint(1) DEFAULT &#39;0&#39;,\n	`remark` varchar(255) DEFAULT NULL,\n	`sort` smallint(6) UNSIGNED DEFAULT NULL,\n	`pid` smallint(6) UNSIGNED NOT NULL,\n	`level` tinyint(1) UNSIGNED NOT NULL,\n	PRIMARY KEY (`id`),\n	KEY `level` (`level`),\n	KEY `pid` (`pid`),\n	KEY `status` (`status`),\n	KEY `name` (`name`)\n) ENGINE = MyISAM CHARSET = utf8;\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="\u6743\u9650\u8BBF\u95EE\u8868" tabindex="-1"><a class="header-anchor" href="#\u6743\u9650\u8BBF\u95EE\u8868" aria-hidden="true">#</a> \u6743\u9650\u8BBF\u95EE\u8868</h3><p>\u6700\u540E\uFF0C\u4E5F\u662F\u6700\u5173\u952E\u7684\u8868 \u6743\u9650\u8BBF\u95EE\u8868\uFF0C\u8FD9\u4E2A\u8868\u4E2D\u7684\u6570\u636E\uFF0C\u662F\u6BCF\u4E2A\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650\u3002\u7406\u89E3\u8FD9\u4E2A\u8868\u7684\u7ED3\u6784\uFF0C\u4E5F\u5C31\u660E\u767D\u4E86\u6574\u4E2A RBAC \u7684\u5173\u952E\u3002</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>CREATE TABLE IF NOT EXISTS `think_access` (\n`role_id` smallint(6) UNSIGNED NOT NULL,\n`node_id` smallint(6) UNSIGNED NOT NULL,\n`level` tinyint(1) NOT NULL,\n`module` varchar(50) DEFAULT NULL,\nKEY `groupId` (`role_id`),\nKEY `nodeId` (`node_id`)\n) ENGINE = MyISAM CHARSET = utf8;\n</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\u901A\u8FC7\u4EE5\u4E0A\u4E94\u5F20\u8868\u5373\u53EF\u5B8C\u6210\u57FA\u4E8E RBAC \u7684\u6743\u9650\u63A7\u5236\u3002\u5F53\u7136\uFF0C\u4E0A\u9762\u63D0\u5230\u7684\u90FD\u662F\u6574\u4E2A\u6743\u9650\u7BA1\u7406\u7684\u57FA\u7840\u6570\u636E\uFF0C\u4E5F\u5C31\u662F\u9700\u8981\u914D\u7F6E\u7684\u6570\u636E\u3002\u4E0B\u4E00\u6B65\uFF0C\u5C31\u662F\u8981\u5728\u7528\u6237\u767B\u5F55\u7684\u65F6\u5019\uFF0C\u83B7\u53D6\u5BF9\u5E94\u7684\u6743\u9650\u3002</p><h2 id="\u83B7\u53D6\u7528\u6237\u7684\u6743\u9650\u5217\u8868\u7684\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u83B7\u53D6\u7528\u6237\u7684\u6743\u9650\u5217\u8868\u7684\u65B9\u6CD5" aria-hidden="true">#</a> \u83B7\u53D6\u7528\u6237\u7684\u6743\u9650\u5217\u8868\u7684\u65B9\u6CD5</h2><ol><li>\u67E5\u8BE2\u7528\u6237\u6240\u5C5E\u7684\u89D2\u8272 ID\uFF1B</li><li>\u6839\u636E\u89D2\u8272 ID \u4ECE ACCESS \u8868\u4E2D\u83B7\u53D6\u8BE5\u89D2\u8272\u6240\u80FD\u8BBF\u95EE\u7684\u8282\u70B9\u5217\u8868\uFF1B</li><li>\u4ECE NODE \u4E2D\u67E5\u8BE2\u8282\u70B9\u5217\u8868\u7684\u76F8\u5173\u4FE1\u606F\uFF1B</li><li>\u5BF9\u4EA7\u751F\u7684\u8282\u70B9\u5217\u8868\u4FE1\u606F\u8FDB\u884C\u5904\u7406\uFF0C\u751F\u6210\u8BBF\u95EE\u51B3\u7B56\u5217\u8868\u4FDD\u5B58\u5230 SESSION \u4E2D\uFF1B</li></ol>',28),s=[l];function r(t,c){return i(),n("div",null,s)}const u=e(d,[["render",r],["__file","\u57FA\u4E8E\u89D2\u8272\u7684\u8BBF\u95EE\u63A7\u5236.html.vue"]]);export{u as default};
