import{_ as i,o as a,c as e,a as l}from"./app.f0c02a19.js";const r="/assets/d8a0f183c602fd4e0411771d6f325cfd.618290cc.jpeg",t="/assets/28ac7af07ba332c29e9a0d91030b0af1.cd1d5499.png",s={},p=l('<h1 id="\u5982\u4F55\u62C6\u5206\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u62C6\u5206\u670D\u52A1" aria-hidden="true">#</a> \u5982\u4F55\u62C6\u5206\u670D\u52A1</h1><p>\u5982\u4ECA\uFF0C\u5E02\u573A\u73AF\u5883\u7EB7\u7E41\u590D\u6742\uFF0C\u77AC\u606F\u4E07\u53D8\uFF0C\u73B0\u4EE3\u4F01\u4E1A\u4E3A\u4E86\u66F4\u597D\u5730\u751F\u5B58\uFF0C\u9700\u8981\u6709\u6781\u5F3A\u7684\u9002\u5E94\u80FD\u529B\u3002<strong>\u5FEB\u901F\u800C\u8F7B\u677E\u5730\u8FCE\u63A5\u6539\u53D8\uFF0C\u6210\u4E3A\u4E86\u4E00\u4E2A\u4F18\u8D28\u4F01\u4E1A\u7684\u7279\u5F81\u4E4B\u4E00</strong>\uFF0C\u540C\u65F6\u4F01\u4E1A\u8FD8\u8981\u6C42\u6280\u672F\u56E2\u961F\u6784\u5EFA\u66F4\u79D1\u5B66\u7684\u67B6\u6784\uFF0C\u642D\u5EFA\u6210\u672C\u66F4\u4F4E\u7684\u5E73\u53F0\uFF0C\u8FD9\u5C31\u4F7F\u5F97\u8FD9\u4E9B\u56E2\u961F\u8D8A\u6765\u8D8A\u503E\u5411\u4E8E\u4F7F\u7528\u5FAE\u670D\u52A1\u67B6\u6784\u6765\u5E94\u5BF9\u4EE5\u4E0A\u8981\u6C42\u3002</p><p>\u5FAE\u670D\u52A1\u7684\u505A\u6CD5\u6709\u5229\u4E8E\u8F6F\u4EF6\u7EC4\u4EF6\u548C\u6570\u636E\u7684\u5206\u6563\u5316\uFF0C\u5C06\u4E00\u4E2A\u6574\u4F53\u5206\u89E3\u6210\u66F4\u5C0F\u3001\u66F4\u5BB9\u6613\u6539\u53D8\u7684\u90E8\u5206\uFF0C<strong>\u5206\u6563\u4EC5\u5E2E\u52A9\u56E2\u961F\u52A0\u5FEB\u5DE5\u7A0B\u8FDB\u5EA6\uFF0C\u800C\u4E0D\u4F1A\u727A\u7272\u7CFB\u7EDF\u7684\u5B89\u5168\u6027</strong>\u3002\u8981\u60F3\u8BA9\u8FD9\u79CD\u67B6\u6784\u5DE5\u4F5C\u5F97\u5F88\u597D\uFF0C\u9700\u8981\u6539\u53D8\u5DE5\u4F5C\u65B9\u5F0F\u3002</p><p>\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u8BBE\u8BA1\uFF0C\u5176\u5B9E\u662F\u4E3A\u4E86\u4F7F\u56E2\u961F\u80FD\u591F\u5728\u6267\u884C\u5DE5\u4F5C\u7684\u4EBA\u4E4B\u95F4\u5206\u914D\u51B3\u7B56\u6743\u529B\uFF0C\u5411\u66F4\u591A\u6210\u5458\u76F4\u63A5\u63A8\u884C\u51B3\u7B56\u6743\uFF0C\u5141\u8BB8\u4ED6\u4EEC\u4EE5\u66F4\u81EA\u7531\u7684\u65B9\u5F0F\u751F\u4EA7\u3002\u5FAE\u670D\u52A1\u67B6\u6784\u4F7F\u7528\u6B63\u786E\u7684\u8BDD\uFF0C\u5C06\u4EA7\u751F\u66F4\u597D\u548C\u66F4\u5FEB\u7684\u53D8\u5316\u3002\u4F46\u662F\u5982\u679C\u4F60\u7684\u67B6\u6784\u9519\u8BEF\uFF0C\u90A3\u4E48\u4E00\u7CFB\u5217\u574F\u7684\u51B3\u5B9A\u53EF\u80FD\u4F1A\u964D\u4F4E\u8F6C\u5316\u7387\uFF0C\u751A\u81F3\u4F1A\u635F\u5BB3\u4F60\u7684\u4E1A\u52A1\u3002</p><p><img src="'+r+'" alt="img"></p><p>\u6211\u4EEC\u8BB2\u51B3\u7B56\u6743\u5206\u914D\uFF0C\u5373\u662F\u8BF4\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u62C6\u5206\u5B9E\u9645\u4E0A\u5C31\u662F\u5728\u5BFB\u6C42\u6B63\u786E\u7684\u6743\u529B\u4E0B\u653E\u6218\u7565\u3002\u8FD9\u662F\u4E00\u4E2A\u8FDB\u5316\u8FC7\u7A0B\uFF0C\u9700\u8981\u4E0D\u65AD\u5730\u8FDB\u884C\u5206\u6790\u548C\u8C03\u6574\u3002\u800C\u5982\u4F55\u6B63\u786E\u7684\u62C6\u5206\u5FAE\u670D\u52A1\u67B6\u6784\uFF0C\u6211\u8BA4\u4E3A\u53EF\u4EE5\u91CD\u70B9\u4ECE\u4EE5\u4E0B\u4E09\u4E2A\u65B9\u9762\u8003\u8651\uFF1A</p><h2 id="_1-\u6211\u4EEC\u5E94\u8BE5\u505A\u54EA\u4E9B\u51B3\u5B9A" tabindex="-1"><a class="header-anchor" href="#_1-\u6211\u4EEC\u5E94\u8BE5\u505A\u54EA\u4E9B\u51B3\u5B9A" aria-hidden="true">#</a> 1. \u6211\u4EEC\u5E94\u8BE5\u505A\u54EA\u4E9B\u51B3\u5B9A\uFF1F</h2><p>\u8BBE\u8BA1\u5FAE\u670D\u52A1\u7CFB\u7EDF\u4E0D\u4EC5\u4EC5\u662F\u6539\u53D8\u7EC4\u4EF6\u5927\u5C0F\uFF0C\u67B6\u6784\u4E2D\u6D89\u53CA\u521B\u5EFA\u548C\u66F4\u6539\u670D\u52A1\u7684\u6240\u6709\u9886\u57DF\u90FD\u6709\u4E00\u5B9A\u7684\u4F5C\u7528\u3002\u5728\u8FD9\u91CC\u603B\u7ED3\u4E86\u4EE5\u4E0B\u4E5D\u4E2A\u65B9\u9762\uFF0C\u4F5C\u4E3A\u62C6\u5206\u5FAE\u670D\u52A1\u67B6\u6784\u65F6\u6240\u505A\u51B3\u5B9A\u7684\u53C2\u8003\uFF1A</p><ul><li>\u751F\u547D\u5468\u671F\uFF1A\u4EC0\u4E48\u65F6\u5019\u521B\u5EFA\u6216\u505C\u6B62\u670D\u52A1\uFF1F\u6211\u4EEC\u4EC0\u4E48\u65F6\u5019\u9700\u8981\u5C06\u5B83\u4EEC\u5206\u5F00\uFF1F</li><li>\u670D\u52A1\u5B9E\u73B0\uFF1A\u6211\u4EEC\u5E94\u8BE5\u5728\u6BCF\u4E2A\u670D\u52A1\u4E2D\u4F7F\u7528\u54EA\u4E9B\u5DE5\u5177\u3001\u8BED\u8A00\u548C\u67B6\u6784\uFF1F</li><li>\u7CFB\u7EDF\u67B6\u6784\uFF1A\u670D\u52A1\u5982\u4F55\u5F15\u5BFC\u4ED6\u4EBA\uFF1F\u5F00\u53D1\u4EBA\u5458\u5982\u4F55\u4E86\u89E3\uFF1F</li><li>\u6570\u636E\u67B6\u6784\uFF1A\u670D\u52A1\u4E4B\u95F4\u5982\u4F55\u5171\u4EAB\u6570\u636E\uFF1F</li><li>\u53D8\u66F4\u8FC7\u7A0B\uFF1A\u4EC0\u4E48\u65F6\u5019\u53EF\u4EE5\u6539\u53D8\u670D\u52A1\uFF1F\u90E8\u7F72\u548C QA \u7684\u5DE5\u5177\u548C\u8FC7\u7A0B\uFF1F</li><li>\u56E2\u961F\u7BA1\u7406\uFF1A\u8C01\u5728\u54EA\u4E2A\u56E2\u961F\u670D\u52A1\uFF1F\u6BCF\u4E2A\u56E2\u961F\u8D1F\u8D23\u4EC0\u4E48\uFF1F\u56E2\u961F\u6210\u5458\u505A\u4E86\u4EC0\u4E48\uFF1F</li><li>\u4EBA\u4E8B\u7BA1\u7406\uFF1A\u4EBA\u5458\u5982\u4F55\u88AB\u96C7\u7528\u548C\u89E3\u96C7\uFF1F\u5458\u5DE5\u5982\u4F55\u6FC0\u52B1\u548C\u5956\u52B1\uFF1F</li><li>\u5B89\u5168\u7BA1\u7406\uFF1A\u6211\u4EEC\u5982\u4F55\u964D\u4F4E\u5B89\u5168\u4E8B\u6545\u7684\u98CE\u9669\uFF1F\u9700\u8981\u505A\u4E9B\u4EC0\u4E48\u6765\u6539\u5584\u6574\u4E2A\u7CFB\u7EDF\u7684\u5B89\u5168\u6027\uFF1F</li><li>\u91C7\u8D2D\u8FC7\u7A0B\uFF1A\u53EF\u4EE5\u8D2D\u4E70\u4EC0\u4E48\u8F6F\u4EF6\uFF1F\u4F7F\u7528\u5F00\u6E90\u8F6F\u4EF6\u9700\u8981\u54EA\u4E9B\u4FDD\u62A4\uFF1F</li></ul><h2 id="_2-\u6D89\u53CA\u5230\u54EA\u4E9B\u4EBA" tabindex="-1"><a class="header-anchor" href="#_2-\u6D89\u53CA\u5230\u54EA\u4E9B\u4EBA" aria-hidden="true">#</a> 2. \u6D89\u53CA\u5230\u54EA\u4E9B\u4EBA\uFF1F</h2><p>\u4E0D\u5F97\u4E0D\u627F\u8BA4\uFF0C\u4E00\u4E2A\u5458\u5DE5\u505A\u51FA\u7684\u4E00\u4E9B\u51B3\u5B9A\u5BF9\u4ED6\u4EEC\u7684\u516C\u53F8\u6765\u8BF4\u53EF\u80FD\u662F\u975E\u5E38\u6709\u5F71\u54CD\u529B\u7684\u3002\u4E00\u4E2A\u5F88\u5178\u578B\u7684\u73B0\u8C61\u662F\uFF0C\u516C\u53F8\u8BD5\u56FE\u5BF9\u5185\u90E8\u7684\u51B3\u7B56\u8005\u589E\u52A0\u63A7\u5236\uFF0C\u4EE5\u4FBF\u5C06\u98CE\u9669\u964D\u81F3\u6700\u4F4E\uFF0C\u4ECE\u800C\u5BFC\u81F4\u51B3\u7B56\u6743\u96C6\u4E2D\u5316\u3002\u4F8B\u5982\uFF0C\u5728\u8FC7\u53BB\u7684\u51E0\u5E74\u4E2D\uFF0C\u82F9\u679C\u4EE5\u62E5\u6709\u4E00\u4E2A\u9AD8\u5EA6\u96C6\u4E2D\u7684\u8BBE\u8BA1\u56E2\u961F\u800C\u95FB\u540D\uFF0C\u5C11\u6570\u7684\u4EBA\u505A\u51FA\u4EA7\u54C1\u8BBE\u8BA1\u7684\u5927\u90E8\u5206\u51B3\u7B56\u3002</p><p>\u96C6\u4E2D\u53D1\u751F\u662F\u56E0\u4E3A\u6B63\u786E\u7684\u4EBA\u9700\u8981\u505A\u51FA\u6700\u91CD\u8981\u7684\u51B3\u5B9A\u3002\u901A\u5E38\uFF0C<strong>\u201C\u6B63\u786E\u7684\u4EBA\u201D\u662F\u5177\u6709\u5929\u8D4B\uFF0C\u4E13\u4E1A\u77E5\u8BC6\u548C\u7ECF\u9A8C\u7684\u7EC4\u5408</strong>\uFF0C\u4F7F\u6211\u4EEC\u80FD\u591F\u76F8\u4FE1\u4ED6\u4EEC\u505A\u51FA\u6700\u597D\u7684\u51B3\u5B9A\uFF0C\u6211\u4EEC\u53EF\u4EE5\u79F0\u8FD9\u4E9B\u4EBA\u4E3A\u6211\u4EEC\u7684\u201C\u660E\u661F\u51B3\u7B56\u8005\u201D\u3002\u4F46\u5B9E\u9645\u4E0A\uFF0C\u516C\u53F8\u7684\u201C\u660E\u661F\u51B3\u7B56\u8005\u201D\u6570\u91CF\u6709\u9650\uFF0C\u5927\u591A\u6570\u56E2\u961F\u53EA\u6709\u51E0\u9897\u201C\u660E\u661F\u201D\u3002</p><p>\u5FAE\u670D\u52A1\u6539\u53D8\u4E86\u8FD9\u79CD\u5C11\u6570\u4EBA\u884C\u4F7F\u51B3\u7B56\u6743\u7684\u73B0\u8C61\uFF0C\u540C\u65F6\u66F4\u5BB9\u6613\u5E94\u5BF9\u9519\u8BEF\u7684\u51B3\u7B56\u3002\u5982\u679C\u4E00\u4E2A\u56E2\u961F\u5728\u5FAE\u670D\u52A1\u5DE5\u4F5C\u65F6\u4F5C\u51FA\u9519\u8BEF\u7684\u51B3\u5B9A\uFF0C\u5176\u9519\u8BEF\u7684\u6CE2\u53CA\u8303\u56F4\u4F1A\u5F88\u5C0F\uFF0C\u5BB9\u9519\u7387\u53D8\u9AD8\u3002\u5F53\u7CFB\u7EDF\u7684\u66F4\u6539\u53D8\u5F97\u4FBF\u5B9C\u548C\u5BB9\u6613\u65F6\uFF0C\u56E2\u961F\u53EF\u4EE5\u5FEB\u901F\u6539\u8FDB\u5148\u524D\u7684\u51B3\u7B56\uFF0C\u4F7F\u4ED6\u4EEC\u80FD\u591F\u66F4\u5FEB\u5730\u83B7\u5F97\u6700\u4F73\u51B3\u7B56\u3002</p><p><img src="'+t+'" alt="img"></p><h2 id="_3-\u8C01\u62E5\u6709\u54EA\u4E00\u90E8\u5206" tabindex="-1"><a class="header-anchor" href="#_3-\u8C01\u62E5\u6709\u54EA\u4E00\u90E8\u5206" aria-hidden="true">#</a> 3. \u8C01\u62E5\u6709\u54EA\u4E00\u90E8\u5206\uFF1F</h2><p>\u51B3\u7B56\u57FA\u4E8E\u9009\u62E9\uFF0C\u800C\u9009\u62E9\u53C8\u57FA\u4E8E\u9886\u57DF\u77E5\u8BC6\u3002\u51B3\u7B56\u4E0D\u5E94\u8BE5\u7ACB\u5373\u6267\u884C\uFF0C\u5B83\u9700\u8981\u4E00\u4E2A\u8FC7\u7A0B\uFF0C\u9700\u8981\u9AD8\u5EA6\u4E13\u4E1A\u7684\u6280\u80FD\u6216\u77E5\u8BC6\u6765\u5B9E\u73B0\u3002</p><p>\u7BA1\u7406\u4E13\u5BB6\u4EA8\u5229\u2022\u660E\u8328\u4F2F\u683C\uFF08Henry Mintzberg\uFF09\u4E3A\u6211\u4EEC\u63D0\u4F9B\u4E86\u4E00\u4E2A\u5F88\u597D\u7684\u6A21\u5F0F\uFF0C\u5176\u4E2D\u6982\u8FF0\u4E86\u51B3\u7B56\u8FC7\u7A0B\u7684\u6B65\u9AA4\uFF1A</p><ul><li>\u7814\u7A76\u4E0E\u4FE1\u606F\u91C7\u96C6</li><li>\u751F\u6210\u9009\u62E9</li><li>\u505A\u51FA\u9009\u62E9</li><li>\u6388\u6743\u7684\u9009\u62E9</li><li>\u6267\u884C\u548C\u5B9E\u65BD</li></ul><p>\u6240\u6709\u8FD9\u4E00\u5207\u7684\u5173\u952E\uFF0C\u662F\u5728\u91C7\u7528\u51B3\u7B56\u5206\u6743\u5316\u65F6\uFF0C\u4E0D\u9700\u8981\u7EDD\u5BF9\u3002\u6BCF\u4E2A\u6B65\u9AA4\u90FD\u53EF\u4EE5\u72EC\u7ACB\u96C6\u4E2D\u6216\u5206\u6563\u5316\uFF0C\u5728\u5E73\u8861\u6548\u7387\u548C\u5B89\u5168\u6027\u65F6\uFF0C\u53EF\u4EE5\u83B7\u5F97\u66F4\u5927\u7684\u7075\u6D3B\u6027\u3002\u5982\u4ECA\uFF0C\u96C6\u4E2D\u9009\u62E9\u4E0E\u975E\u96C6\u4E2D\u9009\u62E9\u76F8\u7ED3\u5408\uFF0C\u4E5F\u662F\u73B0\u5728\u5F88\u591A\u5927\u516C\u53F8\u5E38\u89C1\u7684\u6A21\u5F0F\u3002</p><p>\u5F53\u4EBA\u4EEC\u8C08\u8BBA\u5FAE\u670D\u52A1\u67B6\u6784\u65F6\uFF0C\u6743\u529B\u4E0B\u653E\u8FC7\u7A0B\u7684\u9009\u62E9\u3001\u6388\u6743\u548C\u6267\u884C\u90E8\u5206\u76F8\u4E92\u534F\u4F5C\u3001\u5FEB\u901F\u3001\u89C4\u6A21\u5316\u5730\u79FB\u52A8\uFF0C\u8FD9\u4E9B\u90FD\u964D\u4F4E\u4E86\u53D8\u5316\u6765\u4E34\u65F6\u5BF9\u6574\u4F53\u7CFB\u7EDF\u4EA7\u751F\u7684\u8D1F\u9762\u5F71\u54CD\u3002\u5B83\u662F\u63D0\u9AD8\u53D8\u9769\u901F\u5EA6\u7684\u6709\u6548\u9014\u5F84\uFF0C\u4F46\u4E0D\u8981\u5FD8\u8BB0\uFF0C\u4F60\u7684\u6210\u5458\u7279\u70B9\u3001\u56E2\u961F\u534F\u8C03\uFF0C\u4EE5\u53CA\u6240\u6709\u7684\u7CFB\u7EDF\u3001\u5DE5\u5177\u548C\u5DE5\u4F5C\u73AF\u5883\u540C\u6837\u91CD\u8981\u3002</p><p>\u4F60\u5FC5\u987B\u4E86\u89E3\u5982\u4F55\u505A\u51FA\u51B3\u7B56\uFF0C\u5982\u4F55\u6539\u8FDB\u6D41\u7A0B\uFF0C\u8FD9\u624D\u662F\u8FCE\u63A5\u53D8\u5316\u7684\u597D\u65B9\u6CD5\u3002</p>',21),c=[p];function n(d,_){return a(),e("div",null,c)}const h=i(s,[["render",n],["__file","\u5982\u4F55\u62C6\u5206\u670D\u52A1.html.vue"]]);export{h as default};
