import{_ as e,o as i,c as a,a as r}from"./app.f0c02a19.js";const l={},n=r('<h1 id="\u8BF4\u8BF4\u4F60\u5BF9\u8BBE\u8BA1\u539F\u5219\u7684\u7406\u89E3" tabindex="-1"><a class="header-anchor" href="#\u8BF4\u8BF4\u4F60\u5BF9\u8BBE\u8BA1\u539F\u5219\u7684\u7406\u89E3" aria-hidden="true">#</a> \u8BF4\u8BF4\u4F60\u5BF9\u8BBE\u8BA1\u539F\u5219\u7684\u7406\u89E3</h1><h2 id="\u53E3\u8BC0" tabindex="-1"><a class="header-anchor" href="#\u53E3\u8BC0" aria-hidden="true">#</a> \u53E3\u8BC0</h2><p>\u4E3A\u4E86\u4FBF\u4E8E\u8BB0\u5FC6\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u4E00\u4E2A\u53E3\u8BC0\u6765\u8BB0\u5FC6\u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1\u539F\u5219\uFF1A<strong>\u5F00\u53E3\u5408\u91CC\u6700\u5355\u4F9D</strong></p><ul><li>\u5F00\uFF1A\u5F00\u95ED\u539F\u5219</li><li>\u53E3\uFF1A\u63A5\u53E3\u9694\u79BB\u539F\u5219</li><li>\u5408\uFF1A\u7EC4\u5408/\u805A\u5408\u539F\u5219</li><li>\u91CC\uFF1A\u91CC\u5F0F\u66FF\u6362\u539F\u5219</li><li>\u6700\uFF1A\u6700\u5C11\u77E5\u8BC6\u539F\u5219\uFF08\u8FEA\u7C73\u7279\u6CD5\u5219\uFF09</li><li>\u5355\uFF1A\u5355\u4E00\u804C\u8D23\u539F\u5219</li><li>\u4F9D\uFF1A\u4F9D\u8D56\u5012\u7F6E\u539F\u5219</li></ul><h2 id="\u5F00\u95ED\u539F\u5219-open-closed-principle-ocp" tabindex="-1"><a class="header-anchor" href="#\u5F00\u95ED\u539F\u5219-open-closed-principle-ocp" aria-hidden="true">#</a> \u5F00\u95ED\u539F\u5219(Open-Closed Principle, OCP)</h2><p>\u4E00\u4E2A\u8F6F\u4EF6\u5B9E\u4F53\u5E94\u5F53\u5BF9\u6269\u5C55\u5F00\u53D1,\u5BF9\u4FEE\u6539\u5173\u95ED.\u8BF4\u7684\u662F,\u518D\u8BBE\u8BA1\u4E00\u4E2A\u6A21\u5757\u7684\u65F6\u5019,\u5E94\u5F53\u4F7F\u8FD9\u4E2A\u6A21\u5757\u53EF\u4EE5\u5728\u4E0D\u88AB\u4FEE\u6539\u7684\u524D\u63D0\u4E0B\u88AB\u6269\u5C55.\u6362\u8A00\u4E4B,\u5E94\u5F53\u53EF\u4EE5\u5728\u4E0D\u5FC5\u4FEE\u6539\u6E90\u4EE3\u7801\u7684\u60C5\u51B5\u4E0B\u6539\u53D8\u8FD9\u4E2A\u6A21\u5757\u7684\u884C\u4E3A\uFF0C\u5728\u4FDD\u6301\u7CFB\u7EDF\u4E00\u5B9A\u7A33\u5B9A\u6027\u7684\u57FA\u7840\u4E0A\uFF0C\u5BF9\u7CFB\u7EDF\u8FDB\u884C\u6269\u5C55\u3002\u8FD9\u662F\u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1\uFF08OOD\uFF09\u7684\u57FA\u77F3\uFF0C\u4E5F\u662F\u6700\u91CD\u8981\u7684\u539F\u5219\u3002</p><h2 id="\u63A5\u53E3\u9694\u79BB\u539F\u5219-interface-segregation-principle-isp" tabindex="-1"><a class="header-anchor" href="#\u63A5\u53E3\u9694\u79BB\u539F\u5219-interface-segregation-principle-isp" aria-hidden="true">#</a> \u63A5\u53E3\u9694\u79BB\u539F\u5219(Interface Segregation Principle, ISP)</h2><ul><li>\u4E00\u4E2A\u7C7B\u5BF9\u53E6\u5916\u4E00\u4E2A\u7C7B\u7684\u4F9D\u8D56\u662F\u5EFA\u7ACB\u5728\u6700\u5C0F\u7684\u63A5\u53E3\u4E0A\u3002</li><li>\u4F7F\u7528\u591A\u4E2A\u4E13\u95E8\u7684\u63A5\u53E3\u6BD4\u4F7F\u7528\u5355\u4E00\u7684\u603B\u63A5\u53E3\u8981\u597D.\u6839\u636E\u5BA2\u6237\u9700\u8981\u7684\u4E0D\u540C,\u800C\u4E3A\u4E0D\u540C\u7684\u5BA2\u6237\u7AEF\u63D0\u4F9B\u4E0D\u540C\u7684\u670D\u52A1\u662F\u4E00\u79CD\u5E94\u5F53\u5F97\u5230\u9F13\u52B1\u7684\u505A\u6CD5.\u5C31\u50CF&quot;\u770B\u4EBA\u4E0B\u83DC\u789F&quot;\u4E00\u6837,\u8981\u770B\u5BA2\u4EBA\u662F\u8C01,\u518D\u63D0\u4F9B\u4E0D\u540C\u6863\u6B21\u7684\u996D\u83DC.</li><li>\u80D6\u63A5\u53E3\u4F1A\u5BFC\u81F4\u4ED6\u4EEC\u7684\u5BA2\u6237\u7A0B\u5E8F\u4E4B\u95F4\u4EA7\u751F\u4E0D\u6B63\u5E38\u7684\u5E76\u4E14\u6709\u5BB3\u7684\u8026\u5408\u5173\u7CFB.\u5F53\u4E00\u4E2A\u5BA2\u6237\u7A0B\u5E8F\u8981\u6C42\u8BE5\u80D6\u63A5\u53E3\u8FDB\u884C\u4E00\u4E2A\u6539\u52A8\u65F6,\u4F1A\u5F71\u54CD\u5230\u6240\u6709\u5176\u4ED6\u7684\u5BA2\u6237\u7A0B\u5E8F.\u56E0\u6B64\u5BA2\u6237\u7A0B\u5E8F\u5E94\u8BE5\u4EC5\u4EC5\u4F9D\u8D56\u4ED6\u4EEC\u5B9E\u9645\u9700\u8981\u8C03\u7528\u7684\u65B9\u6CD5.</li></ul><h2 id="\u7EC4\u5408-\u805A\u5408\u590D\u7528\u539F\u5219-composite-aggregate-reuse-principle-carp" tabindex="-1"><a class="header-anchor" href="#\u7EC4\u5408-\u805A\u5408\u590D\u7528\u539F\u5219-composite-aggregate-reuse-principle-carp" aria-hidden="true">#</a> \u7EC4\u5408/\u805A\u5408\u590D\u7528\u539F\u5219(Composite/Aggregate Reuse Principle\uFF0CCARP)</h2><p>\u5728\u4E00\u4E2A\u65B0\u7684\u5BF9\u8C61\u91CC\u9762\u4F7F\u7528\u4E00\u4E9B\u5DF2\u6709\u7684\u5BF9\u8C61,\u4F7F\u4E4B\u6210\u4E3A\u65B0\u5BF9\u8C61\u7684\u4E00\u90E8\u5206;\u65B0\u7684\u5BF9\u8C61\u901A\u8FC7\u8FD9\u4E9B\u5411\u5BF9\u8C61\u7684\u59D4\u6D3E\u8FBE\u5230\u590D\u7528\u5DF2\u6709\u529F\u80FD\u7684\u76EE\u7684.\u8FD9\u4E2A\u8BBE\u8BA1\u539F\u5219\u6709\u53E6\u4E00\u4E2A\u7B80\u77ED\u7684\u8868\u8FF0:\u8981\u5C3D\u91CF\u4F7F\u7528\u5408\u6210/\u805A\u5408,\u5C3D\u91CF\u4E0D\u8981\u4F7F\u7528\u7EE7\u627F.</p><h2 id="\u91CC\u6C0F\u4EE3\u6362\u539F\u5219-liskov-substitution-principle-lsp" tabindex="-1"><a class="header-anchor" href="#\u91CC\u6C0F\u4EE3\u6362\u539F\u5219-liskov-substitution-principle-lsp" aria-hidden="true">#</a> \u91CC\u6C0F\u4EE3\u6362\u539F\u5219(Liskov Substitution Principle\uFF0CLSP)</h2><p>\u7531 Barbar Liskov (\u82AD\u82AD\u62C9.\u91CC\u6C0F) \u63D0\u51FA\uFF0C\u662F\u7EE7\u627F\u590D\u7528\u7684\u57FA\u77F3\u3002</p><p>\u6240\u6709\u5F15\u7528\u57FA\u7C7B\u7684\u5730\u65B9\u5FC5\u987B\u900F\u660E\u7684\u4F7F\u7528\u5176\u5B50\u7C7B\u7684\u5BF9\u8C61\u3002\u53EA\u8981\u7236\u7C7B\u80FD\u51FA\u73B0\u7684\u5730\u65B9\u5B50\u7C7B\u4E5F\u53EF\u4EE5\u51FA\u73B0\uFF0C\u800C\u4E14\u66FF\u6362\u4E3A\u5B50\u7C7B\u4E0D\u4F1A\u4EA7\u751F\u4EFB\u4F55\u9519\u8BEF\u6216\u5F02\u5E38\uFF0C\u4F46\u662F\u53CD\u8FC7\u6765\u5C31\u4E0D\u884C\uFF0C\u6709\u5B50\u7C7B\u51FA\u73B0\u7684\u5730\u65B9\uFF0C\u7236\u7C7B\u672A\u5FC5\u5C31\u80FD\u9002\u5E94\u3002</p><h2 id="\u6700\u5C11\u77E5\u8BC6\u539F\u5219-least-knowledge-principle-lkp" tabindex="-1"><a class="header-anchor" href="#\u6700\u5C11\u77E5\u8BC6\u539F\u5219-least-knowledge-principle-lkp" aria-hidden="true">#</a> \u6700\u5C11\u77E5\u8BC6\u539F\u5219(Least Knowledge Principle\uFF0CLKP)</h2><p>\u4E00\u4E2A\u5BF9\u8C61\u5E94\u5F53\u5BF9\u5176\u4ED6\u5BF9\u8C61\u6709\u5C3D\u53EF\u80FD\u5C11\u7684\u4E86\u89E3.</p><p>\u6CA1\u6709\u4EFB\u4F55\u4E00\u4E2A\u5176\u4ED6\u7684 OO \u8BBE\u8BA1\u539F\u5219\u8C61\u8FEA\u7C73\u7279\u6CD5\u5219\u8FD9\u6837\u6709\u5982\u6B64\u4E4B\u591A\u7684\u8868\u8FF0\u65B9\u5F0F,\u5982\u4E0B\u51E0\u79CD\uFF1A</p><ul><li>\u53EA\u4E0E\u4F60\u76F4\u63A5\u7684\u670B\u53CB\u4EEC\u901A\u4FE1(Only talk to your immediate friends)</li><li>\u4E0D\u8981\u8DDF&quot;\u964C\u751F\u4EBA&quot;\u8BF4\u8BDD(Don&#39;t talk to strangers)</li><li>\u6BCF\u4E00\u4E2A\u8F6F\u4EF6\u5355\u4F4D\u5BF9\u5176\u4ED6\u7684\u5355\u4F4D\u90FD\u53EA\u6709\u6700\u5C11\u7684\u77E5\u8BC6,\u800C\u4E14\u5C40\u9650\u4E8E\u90A3\u4E9B\u672C\u5355\u4F4D\u5BC6\u5207\u76F8\u5173\u7684\u8F6F\u4EF6\u5355\u4F4D</li></ul><p>\u5C31\u662F\u8BF4,\u5982\u679C\u4E24\u4E2A\u7C7B\u4E0D\u5FC5\u5F7C\u6B64\u76F4\u63A5\u901A\u4FE1,\u90A3\u4E48\u8FD9\u4E24\u4E2A\u7C7B\u5C31\u4E0D\u5E94\u5F53\u53D1\u751F\u76F4\u63A5\u7684\u76F8\u4E92\u4F5C\u7528,\u5982\u679C\u5176\u4E2D\u7684\u4E00\u4E2A\u7C7B\u9700\u8981\u8C03\u7528\u53E6\u4E00\u4E2A\u7C7B\u7684\u67D0\u4E00\u4E2A\u65B9\u6CD5\u7684\u8BDD,\u53EF\u4EE5\u901A\u8FC7\u7B2C\u4E09\u8005\u8F6C\u53D1\u8FD9\u4E2A\u8C03\u7528\u3002</p><h2 id="\u5355\u4E00\u804C\u8D23\u539F\u5219-simple-responsibility-pinciple-srp" tabindex="-1"><a class="header-anchor" href="#\u5355\u4E00\u804C\u8D23\u539F\u5219-simple-responsibility-pinciple-srp" aria-hidden="true">#</a> \u5355\u4E00\u804C\u8D23\u539F\u5219(Simple responsibility pinciple\uFF0CSRP)</h2><p>\u5C31\u4E00\u4E2A\u7C7B\u800C\u8A00,\u5E94\u8BE5\u4EC5\u6709\u4E00\u4E2A\u5F15\u8D77\u5B83\u53D8\u5316\u7684\u539F\u56E0,\u5982\u679C\u4F60\u80FD\u60F3\u5230\u591A\u4E8E\u4E00\u4E2A\u7684\u52A8\u673A\u53BB\u6539\u53D8\u4E00\u4E2A\u7C7B,\u90A3\u4E48\u8FD9\u4E2A\u7C7B\u5C31\u5177\u6709\u591A\u4E8E\u4E00\u4E2A\u7684\u804C\u8D23.\u5E94\u8BE5\u628A\u591A\u4E8E\u7684\u6307\u8D23\u5206\u79BB\u51FA\u53BB,\u5206\u522B\u518D\u521B\u5EFA\u4E00\u4E9B\u7C7B\u6765\u5B8C\u6210\u6BCF\u4E00\u4E2A\u804C\u8D23.</p><h2 id="\u4F9D\u8D56\u5012\u7F6E\u539F\u5219-dependence-inversion-principle" tabindex="-1"><a class="header-anchor" href="#\u4F9D\u8D56\u5012\u7F6E\u539F\u5219-dependence-inversion-principle" aria-hidden="true">#</a> \u4F9D\u8D56\u5012\u7F6E\u539F\u5219(Dependence Inversion Principle)</h2><p>\u8981\u6C42\u5BA2\u6237\u7AEF\u4F9D\u8D56\u4E8E\u62BD\u8C61\u8026\u5408.</p><ul><li>\u6A21\u5757\u95F4\u7684\u4F9D\u8D56\u901A\u8FC7\u62BD\u8C61\u53D1\u751F\uFF0C\u5B9E\u73B0\u7C7B\u4E4B\u95F4\u4E0D\u53D1\u751F\u76F4\u63A5\u7684\u4F9D\u8D56\u5173\u7CFB\uFF0C\u5176\u4F9D\u8D56\u5173\u7CFB\u662F\u901A\u8FC7\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B\u4EA7\u751F\u7684\u3002</li><li>\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B\u4E0D\u4F9D\u8D56\u5B9E\u73B0\u7C7B</li><li>\u5B9E\u73B0\u7C7B\u4F9D\u8D56\u63A5\u53E3\u6216\u62BD\u8C61\u7C7B</li></ul><p>\u91C7\u7528\u4F9D\u8D56\u5012\u7F6E\u539F\u5219\u53EF\u4EE5\u51CF\u5C11\u7C7B\u95F4\u7684\u8026\u5408\u6027\uFF0C\u63D0\u9AD8\u7CFB\u7EDF\u7684\u7A33\u5B9A\uFF0C\u964D\u4F4E\u5E76\u884C\u5F00\u53D1\u5F15\u8D77\u7684\u98CE\u9669\uFF0C\u63D0\u9AD8\u4EE3\u7801\u7684\u53EF\u8BFB\u6027\u548C\u53EF\u7EF4\u62A4\u6027\u3002</p>',24),p=[n];function t(s,c){return i(),a("div",null,p)}const d=e(l,[["render",t],["__file","\u8BF4\u8BF4\u4F60\u5BF9\u8BBE\u8BA1\u539F\u5219\u7684\u7406\u89E3.html.vue"]]);export{d as default};
