import{_ as e,o as a,c as r,a as h}from"./app.f0c02a19.js";const i={},s=h('<h1 id="\u9009\u62E9\u5408\u9002\u7684\u6570\u636E\u5B58\u50A8\u65B9\u6848" tabindex="-1"><a class="header-anchor" href="#\u9009\u62E9\u5408\u9002\u7684\u6570\u636E\u5B58\u50A8\u65B9\u6848" aria-hidden="true">#</a> \u9009\u62E9\u5408\u9002\u7684\u6570\u636E\u5B58\u50A8\u65B9\u6848</h1><h2 id="\u5173\u7CFB\u578B\u6570\u636E\u5E93-mysql" tabindex="-1"><a class="header-anchor" href="#\u5173\u7CFB\u578B\u6570\u636E\u5E93-mysql" aria-hidden="true">#</a> \u5173\u7CFB\u578B\u6570\u636E\u5E93 MySQL</h2><p>MySQL \u662F\u4E00\u4E2A\u6700\u6D41\u884C\u7684\u5173\u7CFB\u578B\u6570\u636E\u5E93\uFF0C\u5728\u4E92\u8054\u7F51\u4EA7\u54C1\u4E2D\u5E94\u7528\u6BD4\u8F83\u5E7F\u6CDB\u3002\u4E00\u822C\u60C5\u51B5\u4E0B\uFF0CMySQL \u6570\u636E\u5E93\u662F\u9009\u62E9\u7684\u7B2C\u4E00\u65B9\u6848\uFF0C\u57FA\u672C\u4E0A\u6709 80% ~ 90% \u7684\u573A\u666F\u90FD\u662F\u57FA\u4E8E MySQL \u6570\u636E\u5E93\u7684\u3002\u56E0\u4E3A\uFF0C\u9700\u8981\u5173\u7CFB\u578B\u6570\u636E\u5E93\u8FDB\u884C\u7BA1\u7406\uFF0C\u6B64\u5916\uFF0C\u4E1A\u52A1\u5B58\u5728\u8BB8\u591A\u4E8B\u52A1\u6027\u7684\u64CD\u4F5C\uFF0C\u9700\u8981\u4FDD\u8BC1\u4E8B\u52A1\u7684\u5F3A\u4E00\u81F4\u6027\u3002\u540C\u65F6\uFF0C\u53EF\u80FD\u8FD8\u5B58\u5728\u4E00\u4E9B\u590D\u6742\u7684 SQL \u7684\u67E5\u8BE2\u3002\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u524D\u671F\u5C3D\u91CF\u51CF\u5C11\u8868\u7684\u8054\u5408\u67E5\u8BE2\uFF0C\u4FBF\u4E8E\u540E\u671F\u6570\u636E\u91CF\u589E\u5927\u7684\u60C5\u51B5\u4E0B\uFF0C\u505A\u6570\u636E\u5E93\u7684\u5206\u5E93\u5206\u8868\u3002</p><h2 id="\u5185\u5B58\u6570\u636E\u5E93-redis" tabindex="-1"><a class="header-anchor" href="#\u5185\u5B58\u6570\u636E\u5E93-redis" aria-hidden="true">#</a> \u5185\u5B58\u6570\u636E\u5E93 Redis</h2><p>\u968F\u7740\u6570\u636E\u91CF\u7684\u589E\u957F\uFF0CMySQL \u5DF2\u7ECF\u6EE1\u8DB3\u4E0D\u4E86\u5927\u578B\u4E92\u8054\u7F51\u7C7B\u5E94\u7528\u7684\u9700\u6C42\u3002\u56E0\u6B64\uFF0CRedis \u57FA\u4E8E\u5185\u5B58\u5B58\u50A8\u6570\u636E\uFF0C\u53EF\u4EE5\u6781\u5927\u7684\u63D0\u9AD8\u67E5\u8BE2\u6027\u80FD\uFF0C\u5BF9\u4EA7\u54C1\u5728\u67B6\u6784\u4E0A\u5F88\u597D\u7684\u8865\u5145\u3002\u4F8B\u5982\uFF0C\u4E3A\u4E86\u63D0\u9AD8\u670D\u52A1\u7AEF\u63A5\u53E3\u7684\u8BBF\u95EE\u901F\u5EA6\uFF0C\u5C3D\u53EF\u80FD\u5C06\u8BFB\u9891\u7387\u9AD8\u7684\u70ED\u70B9\u6570\u636E\u5B58\u653E\u5728 Redis \u4E2D\u3002\u8FD9\u4E2A\u662F\u975E\u5E38\u5178\u578B\u7684\u4EE5\u7A7A\u95F4\u6362\u65F6\u95F4\u7684\u7B56\u7565\uFF0C\u4F7F\u7528\u66F4\u591A\u7684\u5185\u5B58\u6362\u53D6 CPU \u8D44\u6E90\uFF0C\u901A\u8FC7\u589E\u52A0\u7CFB\u7EDF\u7684\u5185\u5B58\u6D88\u8017\uFF0C\u6765\u52A0\u5FEB\u7A0B\u5E8F\u7684\u8FD0\u884C\u901F\u5EA6\u3002</p><p>\u5728\u67D0\u4E9B\u573A\u666F\u4E0B\uFF0C\u53EF\u4EE5\u5145\u5206\u7684\u5229\u7528 Redis \u7684\u7279\u6027\uFF0C\u5927\u5927\u63D0\u9AD8\u6548\u7387\u3002\u8FD9\u4E9B\u573A\u666F\u5305\u62EC\u7F13\u5B58\uFF0C\u4F1A\u8BDD\u7F13\u5B58\uFF0C\u65F6\u6548\u6027\uFF0C\u8BBF\u95EE\u9891\u7387\uFF0C\u8BA1\u6570\u5668\uFF0C\u793E\u4EA4\u5217\u8868\uFF0C\u8BB0\u5F55\u7528\u6237\u5224\u5B9A\u4FE1\u606F\uFF0C\u4EA4\u96C6\u3001\u5E76\u96C6\u548C\u5DEE\u96C6\uFF0C\u70ED\u95E8\u5217\u8868\u4E0E\u6392\u884C\u699C\uFF0C\u6700\u65B0\u52A8\u6001\u7B49\u3002</p><p>\u4F7F\u7528 Redis \u505A\u7F13\u5B58\u7684\u65F6\u5019\uFF0C\u9700\u8981\u8003\u8651\u6570\u636E\u4E0D\u4E00\u81F4\u4E0E\u810F\u8BFB\u3001\u7F13\u5B58\u66F4\u65B0\u673A\u5236\u3001\u7F13\u5B58\u53EF\u7528\u6027\u3001\u7F13\u5B58\u670D\u52A1\u964D\u7EA7\u3001\u7F13\u5B58\u7A7F\u900F\u3001\u7F13\u5B58\u9884\u70ED\u7B49\u7F13\u5B58\u4F7F\u7528\u95EE\u9898\u3002</p><h2 id="\u6587\u6863\u6570\u636E\u5E93-mongodb" tabindex="-1"><a class="header-anchor" href="#\u6587\u6863\u6570\u636E\u5E93-mongodb" aria-hidden="true">#</a> \u6587\u6863\u6570\u636E\u5E93 MongoDB</h2><p>MongoDB \u662F\u5BF9\u4F20\u7EDF\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7684\u8865\u5145\uFF0C\u5B83\u975E\u5E38\u9002\u5408\u9AD8\u4F38\u7F29\u6027\u7684\u573A\u666F\uFF0C\u5B83\u662F\u53EF\u6269\u5C55\u6027\u7684\u8868\u7ED3\u6784\u3002\u57FA\u4E8E\u8FD9\u70B9\uFF0C\u53EF\u4EE5\u5C06\u9884\u671F\u8303\u56F4\u5185\uFF0C\u8868\u7ED3\u6784\u53EF\u80FD\u4F1A\u4E0D\u65AD\u6269\u5C55\u7684 MySQL \u8868\u7ED3\u6784\uFF0C\u901A\u8FC7 MongoDB \u6765\u5B58\u50A8\uFF0C\u8FD9\u5C31\u53EF\u4EE5\u4FDD\u8BC1\u8868\u7ED3\u6784\u7684\u6269\u5C55\u6027\u3002</p><p>\u6B64\u5916\uFF0C\u65E5\u5FD7\u7CFB\u7EDF\u6570\u636E\u91CF\u7279\u522B\u5927\uFF0C\u5982\u679C\u7528 MongoDB \u6570\u636E\u5E93\u5B58\u50A8\u8FD9\u4E9B\u6570\u636E\uFF0C\u5229\u7528\u5206\u7247\u96C6\u7FA4\u652F\u6301\u6D77\u91CF\u6570\u636E\uFF0C\u540C\u65F6\u4F7F\u7528\u805A\u96C6\u5206\u6790\u548C MapReduce \u7684\u80FD\u529B\uFF0C\u662F\u4E2A\u5F88\u597D\u7684\u9009\u62E9\u3002</p><p>MongoDB \u8FD8\u9002\u5408\u5B58\u50A8\u5927\u5C3A\u5BF8\u7684\u6570\u636E\uFF0CGridFS \u5B58\u50A8\u65B9\u6848\u5C31\u662F\u57FA\u4E8E MongoDB \u7684\u5206\u5E03\u5F0F\u6587\u4EF6\u5B58\u50A8\u7CFB\u7EDF\u3002</p><h2 id="\u5217\u65CF\u6570\u636E\u5E93-hbase" tabindex="-1"><a class="header-anchor" href="#\u5217\u65CF\u6570\u636E\u5E93-hbase" aria-hidden="true">#</a> \u5217\u65CF\u6570\u636E\u5E93 HBase</h2><p>HBase \u9002\u5408\u6D77\u91CF\u6570\u636E\u7684\u5B58\u50A8\u4E0E\u9AD8\u6027\u80FD\u5B9E\u65F6\u67E5\u8BE2\uFF0C\u5B83\u662F\u8FD0\u884C\u4E8E HDFS \u6587\u4EF6\u7CFB\u7EDF\u4E4B\u4E0A\uFF0C\u5E76\u4E14\u4F5C\u4E3A MapReduce \u5206\u5E03\u5F0F\u5904\u7406\u7684\u76EE\u6807\u6570\u636E\u5E93\uFF0C\u4EE5\u652F\u6491\u79BB\u7EBF\u5206\u6790\u578B\u5E94\u7528\u3002\u5728\u6570\u636E\u4ED3\u5E93\u3001\u6570\u636E\u96C6\u5E02\u3001\u5546\u4E1A\u667A\u80FD\u7B49\u9886\u57DF\u53D1\u6325\u4E86\u8D8A\u6765\u8D8A\u591A\u7684\u4F5C\u7528\uFF0C\u5728\u6570\u4EE5\u5343\u8BA1\u7684\u4F01\u4E1A\u4E2D\u652F\u6491\u7740\u5927\u91CF\u7684\u5927\u6570\u636E\u5206\u6790\u573A\u666F\u7684\u5E94\u7528\u3002</p><h2 id="\u5168\u6587\u641C\u7D22\u5F15\u64CE-elasticsearch" tabindex="-1"><a class="header-anchor" href="#\u5168\u6587\u641C\u7D22\u5F15\u64CE-elasticsearch" aria-hidden="true">#</a> \u5168\u6587\u641C\u7D22\u5F15\u64CE ElasticSearch</h2><p>\u5728\u4E00\u822C\u60C5\u51B5\u4E0B\uFF0C\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7684\u6A21\u7CCA\u67E5\u8BE2\uFF0C\u90FD\u662F\u901A\u8FC7 like \u7684\u65B9\u5F0F\u8FDB\u884C\u67E5\u8BE2\u3002\u5176\u4E2D\uFF0Clike \u201Cvalue%\u201D \u53EF\u4EE5\u4F7F\u7528\u7D22\u5F15\uFF0C\u4F46\u662F\u5BF9\u4E8E like \u201C%value%\u201D \u8FD9\u6837\u7684\u65B9\u5F0F\uFF0C\u6267\u884C\u5168\u8868\u67E5\u8BE2\uFF0C\u8FD9\u5728\u6570\u636E\u91CF\u5C0F\u7684\u8868\uFF0C\u4E0D\u5B58\u5728\u6027\u80FD\u95EE\u9898\uFF0C\u4F46\u662F\u5BF9\u4E8E\u6D77\u91CF\u6570\u636E\uFF0C\u5168\u8868\u626B\u63CF\u662F\u975E\u5E38\u53EF\u6015\u7684\u4E8B\u60C5\u3002ElasticSearch \u4F5C\u4E3A\u4E00\u4E2A\u5EFA\u7ACB\u5728\u5168\u6587\u641C\u7D22\u5F15\u64CE Apache Lucene \u57FA\u7840\u4E0A\u7684\u5B9E\u65F6\u7684\u5206\u5E03\u5F0F\u641C\u7D22\u548C\u5206\u6790\u5F15\u64CE\uFF0C\u9002\u7528\u4E8E\u5904\u7406\u5B9E\u65F6\u641C\u7D22\u5E94\u7528\u573A\u666F\u3002\u6B64\u5916\uFF0C\u4F7F\u7528 ElasticSearch \u5168\u6587\u641C\u7D22\u5F15\u64CE\uFF0C\u8FD8\u53EF\u4EE5\u652F\u6301\u591A\u8BCD\u6761\u67E5\u8BE2\u3001\u5339\u914D\u5EA6\u4E0E\u6743\u91CD\u3001\u81EA\u52A8\u8054\u60F3\u3001\u62FC\u5199\u7EA0\u9519\u7B49\u9AD8\u7EA7\u529F\u80FD\u3002\u56E0\u6B64\uFF0C\u53EF\u4EE5\u4F7F\u7528 ElasticSearch \u4F5C\u4E3A\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5168\u6587\u641C\u7D22\u7684\u529F\u80FD\u8865\u5145\uFF0C\u5C06\u8981\u8FDB\u884C\u5168\u6587\u641C\u7D22\u7684\u6570\u636E\u7F13\u5B58\u4E00\u4EFD\u5230 ElasticSearch \u4E0A\uFF0C\u8FBE\u5230\u5904\u7406\u590D\u6742\u7684\u4E1A\u52A1\u4E0E\u63D0\u9AD8\u67E5\u8BE2\u901F\u5EA6\u7684\u76EE\u7684\u3002</p><p>ElasticSearch \u4E0D\u4EC5\u4EC5\u9002\u7528\u4E8E\u641C\u7D22\u573A\u666F\uFF0C\u8FD8\u975E\u5E38\u9002\u5408\u65E5\u5FD7\u5904\u7406\u4E0E\u5206\u6790\u7684\u573A\u666F\u3002<strong>\u8457\u540D\u7684 ELK \u65E5\u5FD7\u5904\u7406\u65B9\u6848\uFF0C\u7531 ElasticSearch\u3001LogStash \u548C KibAna \u4E09\u4E2A\u7EC4\u4EF6\u7EC4\u6210</strong>\uFF0C\u5305\u62EC\u4E86\u65E5\u5FD7\u6536\u96C6\u3001\u805A\u5408\u3001\u591A\u7EF4\u5EA6\u67E5\u8BE2\u3001\u53EF\u89C6\u5316\u663E\u793A\u7B49\u3002</p>',16),c=[s];function d(t,n){return a(),r("div",null,c)}const l=e(i,[["render",d],["__file","\u9009\u62E9\u5408\u9002\u7684\u6570\u636E\u5B58\u50A8\u65B9\u6848.html.vue"]]);export{l as default};
